mapper.update:
update yb_chs_apply_task
applyDateStr=#{applyDateStr}, startNum=#{startNum}, endNum=#{endNum}, totalRow=#{totalRow}, currentPage=#{currentPage}, pageSize=#{pageSize}, totalPage=#{totalPage}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, areaType=#{areaType}, hisCount=#{hisCount}, isOutpfees=#{isOutpfees},
where id=#{id}

mapper.select:
select * from yb_chs_apply_task
where 1=1
and id =#{ ybChsApplyTask.id}
and applyDateStr like
    concat('%', #{ ybChsApplyTask.applyDateStr} ,'%')

and startNum =#{ ybChsApplyTask.startNum}
and endNum =#{ ybChsApplyTask.endNum}
and totalRow =#{ ybChsApplyTask.totalRow}
and currentPage =#{ ybChsApplyTask.currentPage}
and pageSize =#{ ybChsApplyTask.pageSize}
and totalPage =#{ ybChsApplyTask.totalPage}
and COMMENTS like concat('%',
    #{ ybChsApplyTask.comments} ,'%')

and STATE =#{ ybChsApplyTask.state}
and IS_DELETEMARK =#{ ybChsApplyTask.isDeletemark}

    AND ybChsApplyTask.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybChsApplyTask.MODIFY_TIME  <=   modifyTimeTo}


    AND ybChsApplyTask.CREATE_TIME  >=   createTimeFrom}


    AND ybChsApplyTask.CREATE_TIME  <=   createTimeTo}

and areaType =#{ ybChsApplyTask.areaType}
and hisCount =#{ ybChsApplyTask.hisCount}
and isOutpfees =#{ ybChsApplyTask.isOutpfees}
