mapper.update:
update yb_chs_confire
doctorCode=#{doctorCode}, doctorName=#{doctorName}, adminType=#{adminType}, currencyField=#{currencyField}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, areaType=#{areaType}, operatorId=#{operatorId}, operatorName=#{operatorName},
where id=#{id}

mapper.select:
select * from yb_chs_confire
where 1=1
and id =#{ ybChsConfire.id}
and doctorCode like concat('%',
    #{ ybChsConfire.doctorCode} ,'%')

and doctorName like concat('%',
    #{ ybChsConfire.doctorName} ,'%')

and adminType =#{ ybChsConfire.adminType}
and currencyField like
    concat('%', #{ ybChsConfire.currencyField} ,'%')

and COMMENTS like concat('%',
    #{ ybChsConfire.comments} ,'%')

and STATE =#{ ybChsConfire.state}
and IS_DELETEMARK =#{ ybChsConfire.isDeletemark}

    AND ybChsConfire.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybChsConfire.MODIFY_TIME  <=   modifyTimeTo}


    AND ybChsConfire.CREATE_TIME  >=   createTimeFrom}


    AND ybChsConfire.CREATE_TIME  <=   createTimeTo}

and areaType =#{ ybChsConfire.areaType}
and operatorName like
    concat('%', #{ ybChsConfire.operatorName} ,'%')

SELECT count(1) FROM yb_chs_confire where IS_DELETEMARK = 1
AND areaType = #{ areaType }
and adminType = #{ adminType }
and (doctorCode like concat('%', #{ doctorContent } ,'%') or doctorName like concat('%', #{ doctorContent } ,'%'))
and id in(select DISTINCT pid from yb_chs_confire_data where deptName like concat('%', #{ deptContent } ,'%'))
and operatorName like concat('%', #{ operatorName } ,'%')

SELECT * FROM yb_chs_confire where doctorCode= #{ ybChsConfire.doctorCode }

    and id != #{ ybChsConfire.id }


    and areaType = #{ ybChsConfire.areaType }

and adminType in (
select id from com_type where ctName like concat('%', #{ ybChsConfire.comments } ,'%')
)

SELECT count(1) FROM yb_chs_confire where IS_DELETEMARK = 1
AND areaType = #{ areaType }
and adminType = #{ adminType }
and CREATE_USER_ID = #{ uid }
and (CREATE_USER_ID = #{ uid } or doctorCode=#{ doctorCode })
and (doctorCode like concat('%', #{ doctorContent } ,'%') or doctorName like concat('%', #{ doctorContent } ,'%'))
and id in(select DISTINCT pid from yb_chs_confire_data where deptName like concat('%', #{ deptContent } ,'%'))
and operatorName like concat('%', #{ operatorName } ,'%')
