mapper.update:
update yb_chs_manage
applyDataId=#{applyDataId}, verifyId=#{verifyId}, readyDoctorCode=#{readyDoctorCode}, readyDoctorName=#{readyDoctorName}, readyDksId=#{readyDksId}, readyDksName=#{readyDksName}, changeDoctorCode=#{changeDoctorCode}, changeDoctorName=#{changeDoctorName}, changeDksId=#{changeDksId}, changeDksName=#{changeDksName}, operateReason=#{operateReason}, operateDate=#{operateDate}, refuseId=#{refuseId}, refuseName=#{refuseName}, refuseReason=#{refuseReason}, refuseDate=#{refuseDate}, adminPersonId=#{adminPersonId}, adminPersonName=#{adminPersonName}, adminChangeDate=#{adminChangeDate}, adminReason=#{adminReason}, STATE=#{state}, enableDate=#{enableDate}, operateProcess=#{operateProcess}, approvalState=#{approvalState}, applyDateStr=#{applyDateStr}, orderNum=#{orderNum}, areaType=#{areaType}, dataType=#{dataType}, readyFyid=#{readyFyid}, changeFyid=#{changeFyid},
where id=#{id}

mapper.select:
select * from yb_chs_manage where 1=1 and id =#{ ybChsManage.id} and applyDataId like concat('%', #{ ybChsManage.applyDataId} ,'%')
and verifyId like concat('%', #{ ybChsManage.verifyId} ,'%')
and readyDoctorCode like concat('%', #{ ybChsManage.readyDoctorCode} ,'%')
and readyDoctorName like concat('%', #{ ybChsManage.readyDoctorName} ,'%')
and readyDksId like concat('%', #{ ybChsManage.readyDksId} ,'%')
and readyDksName like concat('%', #{ ybChsManage.readyDksName} ,'%')
and changeDoctorCode like concat('%', #{ ybChsManage.changeDoctorCode} ,'%')
and changeDoctorName like concat('%', #{ ybChsManage.changeDoctorName} ,'%')
and changeDksId like concat('%', #{ ybChsManage.changeDksId} ,'%')
and changeDksName like concat('%', #{ ybChsManage.changeDksName} ,'%')
and operateReason like concat('%', #{ ybChsManage.operateReason} ,'%')
AND ybChsManage.operateDate >= operateDateFrom}
AND ybChsManage.operateDate <= operateDateTo}
and refuseName like concat('%', #{ ybChsManage.refuseName} ,'%')
and refuseReason like concat('%', #{ ybChsManage.refuseReason} ,'%')
AND ybChsManage.refuseDate >= refuseDateFrom}
AND ybChsManage.refuseDate <= refuseDateTo}
and adminPersonName like concat('%', #{ ybChsManage.adminPersonName} ,'%')
AND ybChsManage.adminChangeDate >= adminChangeDateFrom}
AND ybChsManage.adminChangeDate <= adminChangeDateTo}
and adminReason like concat('%', #{ ybChsManage.adminReason} ,'%')
and STATE =#{ ybChsManage.state}
AND ybChsManage.enableDate >= enableDateFrom}
AND ybChsManage.enableDate <= enableDateTo}
and operateProcess like concat('%', #{ ybChsManage.operateProcess} ,'%')
and approvalState =#{ ybChsManage.approvalState} and applyDateStr like concat('%', #{ ybChsManage.applyDateStr} ,'%')
and orderNum =#{ ybChsManage.orderNum} and areaType =#{ ybChsManage.areaType}
