mapper.select:
select cr.id, ad.id applyDataId, ad.pid, ad.appealEndDate, ad.orderNum, ra.applyDateStr, ra.endDate applyEndDate, ad.payPlaceType, ad.ydState, ad.areaName, ad.yyjgCode, ad.yyjgName, ad.deptName, ad.doctorName, ad.medicalType, ad.zymzNumber, ad.insuredName, ad.enterHospitalDate, ad.outHospitalDate, ad.settlementDate, ad.cardNumber, ad.projectCode, ad.projectName, ad.projectYyName, ad.ruleName, ad.violateCsPrice, ad.violatePrice, ad.violateReason, ad.zdNote, ad.costDate, ad.insuredType, ad.num, ad.price, ad.medicalPrice, ad.tcPayPrice, ad.specs, ad.jx, ad.jgLevel, cr.doctorCode resultDoctorCode, cr.doctorName resultDoctorName, cr.dksId resultDksId, cr.dksName resultDksName, cr.operateReason, cr.operateDate, cr.state, cr.areaType from yb_chs_result cr LEFT JOIN yb_chs_apply_data ad ON cr.applyDataId = ad.id and ad.pid = #{ybChsResultView.pid} LEFT JOIN yb_chs_apply ra ON ra.id = ad.pid where cr.applyDateStr = #{ybChsResultView.applyDateStr}
and cr.areaType = #{ybChsResultView.areaType}
and cr.state = #{ybChsResultView.state}
order by cr.orderNum

select
count(1)
from
yb_chs_result cr
LEFT JOIN yb_chs_apply_data ad ON
cr.applyDataId = ad.id and ad.pid = #{ybChsResultView.pid}
where
cr.applyDateStr = #{ybChsResultView.applyDateStr}

    and cr.areaType = #{ybChsResultView.areaType}


    and cr.state = #{ybChsResultView.state}
