mapper.select:
select case when rv.id is null then UUID() else rv.id end id, ad.pid, ad.id applyDataId, #{ ybChsVerifyView.applyDateStr} applyDateStr, ad.appealEndDate, ad.payPlaceType, ad.ydState, ad.areaName, ad.yyjgCode, ad.yyjgName, ad.deptName, ad.doctorName, ad.medicalType, ad.zymzNumber, ad.insuredName, ad.enterHospitalDate, ad.outHospitalDate, ad.settlementDate, ad.cardNumber, ad.projectCode, ad.projectName, ad.projectYyName, ad.ruleName, ad.violateCsPrice, ad.violatePrice, ad.violateReason, ad.zdNote, ad.costDate, ad.insuredType, ad.num, ad.price, ad.medicalPrice, ad.tcPayPrice, ad.specs, ad.jx, ad.jgLevel, ad.orderNum, rv.verifyDoctorCode, concat(rv.verifyDoctorCode,'-',rv.verifyDoctorName) verifyDoctorName, rv.verifyDksId, concat(rv.verifyDksId,'-',rv.verifyDksName) verifyDksName, case when rv.id is null then 0 else 1 end isVerify, case when yp.id is null then 0 else 1 end isPerson, rv.sendPersonId, rv.sendPersonName, rv.sendDate, rv.STATE, rv.areaType from yb_chs_verify rv INNER JOIN yb_chs_apply_data ad ON ad.id = rv.applyDataId and ad.pid = #{ ybChsVerifyView.pid}
and ad.orderNum = #{ ybChsVerifyView.orderNum}
and ad.zymzNumber = #{ ybChsVerifyView.zymzNumber}
and ad.insuredName = #{ ybChsVerifyView.insuredName}
and ad.projectName like concat('%', #{ ybChsVerifyView.projectName} ,'%')
LEFT join yb_person yp on rv.verifyDoctorCode = yp.personCode where rv.applyDateStr = #{ ybChsVerifyView.applyDateStr} and rv.areaType = #{ ybChsVerifyView.areaType} and rv.state =#{ ybChsVerifyView.state}
and rv.orderNum = #{ ybChsVerifyView.orderNum}
and rv.verifyDoctorCode = #{ ybChsVerifyView.verifyDoctorCode}
and rv.verifyDoctorName = #{ ybChsVerifyView.verifyDoctorName}
and rv.verifyDksId = #{ ybChsVerifyView.verifyDksId}
and rv.verifyDksName = #{ ybChsVerifyView.verifyDksName}
order by rv.orderNum

select
count(1)
from
yb_chs_verify rv
inner JOIN yb_chs_apply_data ad ON
ad.id = rv.applyDataId and
ad.pid = #{ ybChsVerifyView.pid}

    and ad.orderNum = #{ ybChsVerifyView.orderNum}


    and ad.zymzNumber = #{ ybChsVerifyView.zymzNumber}


    and ad.insuredName = #{ ybChsVerifyView.insuredName}


    and ad.projectName like concat('%', #{ ybChsVerifyView.projectName} ,'%')

where
rv.applyDateStr = #{ ybChsVerifyView.applyDateStr}
and rv.areaType = #{ ybChsVerifyView.areaType}
and rv.state =#{ ybChsVerifyView.state}

    and rv.orderNum = #{ ybChsVerifyView.orderNum}


    and rv.verifyDoctorCode = #{ ybChsVerifyView.verifyDoctorCode}


    and rv.verifyDoctorName = #{ ybChsVerifyView.verifyDoctorName}


    and rv.verifyDksId = #{ ybChsVerifyView.verifyDksId}


    and rv.verifyDksName = #{ ybChsVerifyView.verifyDksName}
