mapper.update:
update yb_dks
dksId=#{dksId}, dksName=#{dksName}, spellCode=#{spellCode}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select * from yb_dks
where 1=1
and id =#{ ybDks.id}
and dksId like concat('%', #{ ybDks.dksId} ,'%')
and dksName like concat('%', #{ ybDks.dksName} ,'%')

and spellCode like concat('%', #{ ybDks.spellCode}
    ,'%')

and COMMENTS like concat('%', #{ ybDks.comments}
    ,'%')

and STATE =#{ ybDks.state}
and IS_DELETEMARK =#{ ybDks.isDeletemark}

    AND ybDks.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybDks.MODIFY_TIME  <=   modifyTimeTo}


    AND ybDks.CREATE_TIME  >=   createTimeFrom}


    AND ybDks.CREATE_TIME  <=   createTimeTo}

select * from yb_dks where dksId in(
select dksId from yb_chs_confire_data where pid = #{chsConfireId}
) and IS_DELETEMARK = 1

    and (dksName like concat('%', #{ comments} ,'%') or spellCode like concat('%', #{ comments} ,'%'))
