mapper.update:
update com_configuremanage
intField=#{intField}, numericField=#{numericField}, stringField=#{stringField}, currencyField=#{currencyField}, configureType=#{configureType}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select  *  from com_configuremanage
where  1=1
 and id =#{ comConfiguremanage.id} 
 and intField =#{ comConfiguremanage.intField} 
     and stringField like concat('%', #{ comConfiguremanage.stringField} ,'%')
     and currencyField like concat('%', #{ comConfiguremanage.currencyField} ,'%')
 and configureType =#{ comConfiguremanage.configureType} 
     and COMMENTS like concat('%', #{ comConfiguremanage.comments} ,'%')
 and STATE =#{ comConfiguremanage.state} 
 and IS_DELETEMARK =#{ comConfiguremanage.isDeletemark} 

    AND comConfiguremanage.MODIFY_TIME  >=   modifyTimeFrom}


    AND comConfiguremanage.MODIFY_TIME  <=   modifyTimeTo}


    AND comConfiguremanage.CREATE_TIME  >=   createTimeFrom}


    AND comConfiguremanage.CREATE_TIME  <=   createTimeTo}
