mapper.update:
update com_file
CLIENT_NAME=#{clientName}, SERVER_NAME=#{serverName}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, REF_TAB_ID=#{refTabId}, REF_TAB_TABLE=#{refTabTable}, REF_TYPE=#{refType},
where ID=#{id}

mapper.select:
select * from com_file
where 1=1
and ID =#{ comFile.id}
and CLIENT_NAME like concat('%',
    #{ comFile.clientName} ,'%')

and SERVER_NAME like concat('%',
    #{ comFile.serverName} ,'%')

and IS_DELETEMARK =#{ comFile.isDeletemark}

    AND comFile.CREATE_TIME  >=   createTimeFrom}


    AND comFile.CREATE_TIME  <=   createTimeTo}


    AND comFile.MODIFY_TIME  >=   modifyTimeFrom}


    AND comFile.MODIFY_TIME  <=   modifyTimeTo}

and CREATE_USER_ID =#{ comFile.createUserId}
and MODIFY_USER_ID =#{ comFile.modifyUserId}
and REF_TAB_ID like concat('%',
    #{ comFile.refTabId} ,'%')

and REF_TAB_TABLE like concat('%',
    #{ comFile.refTabTable} ,'%')

and REF_TYPE =#{ comFile.refType}

SELECT
com_file.*
FROM
com_file
INNER JOIN yb_appeal_result ON
com_file.REF_TAB_ID = yb_appeal_result.id
WHERE
com_file.IS_DELETEMARK = 1
and com_file.REF_TAB_ID = #{inUploadFile.id}
and yb_appeal_result.applyDateStr = #{inUploadFile.applyDateStr}

and yb_appeal_result.areaType = #{inUploadFile.areaType}
and yb_appeal_result.sourceType = #{inUploadFile.sourceType}
and yb_appeal_result.typeno =
    #{inUploadFile.typeno}

and yb_appeal_result.dataType = #{inUploadFile.dataType}
and yb_appeal_result.STATE IN (1,2)
and yb_appeal_result.STATE = 1
and yb_appeal_result.STATE = 2
and yb_appeal_result.deptCode = #{inUploadFile.deptId}

SELECT
com_file.*
FROM
com_file
INNER JOIN yb_appeal_result ON
com_file.REF_TAB_ID = yb_appeal_result.id
WHERE
com_file.IS_DELETEMARK = 1
and com_file.REF_TAB_ID = #{inUploadFile.id}
and yb_appeal_result.applyDateStr = #{inUploadFile.applyDateStr}

and yb_appeal_result.areaType = #{inUploadFile.areaType}
and yb_appeal_result.sourceType = #{inUploadFile.sourceType}
and yb_appeal_result.typeno =
    #{inUploadFile.typeno}

and yb_appeal_result.dataType = #{inUploadFile.dataType}
and yb_appeal_result.STATE IN (1,2)
and yb_appeal_result.STATE = 1
and yb_appeal_result.STATE = 2
and yb_appeal_result.deptCode in (select deptId from
    yb_appeal_sumdept_data where pid = #{inUploadFile.sumId})
