mapper.update:
update com_sms
sendcode=#{sendcode}, sendname=#{sendname}, mobile=#{mobile}, sendType=#{sendType}, operatorId=#{operatorId}, operatorName=#{operatorName}, sendcontent=#{sendcontent}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, areaType=#{areaType}, applyDateStr=#{applyDateStr}, typeno=#{typeno}, refTableId=#{refTableId},
where id=#{id}

mapper.select:
select  *  from com_sms
where  1=1
     and id =#{ comSms.id} 
 and applyDateStr =#{ comSms.applyDateStr} 
 and typeno =#{ comSms.typeno} 
     and sendcode like concat('%', #{ comSms.sendcode} ,'%')
     and sendname like concat('%', #{ comSms.sendname} ,'%')
     and mobile like concat('%', #{ comSms.mobile} ,'%')
 and sendType =#{ comSms.sendType} 
     and operatorName like concat('%', #{ comSms.operatorName} ,'%')
     and sendcontent like concat('%', #{ comSms.sendcontent} ,'%')
     and COMMENTS like concat('%', #{ comSms.comments} ,'%')
 and STATE =#{ comSms.state} 
 and areaType =#{ comSms.areaType} 
 and refTableId =#{ comSms.refTableId} 
 and IS_DELETEMARK =#{ comSms.isDeletemark} 

    AND comSms.MODIFY_TIME  >=   modifyTimeFrom}


    AND comSms.MODIFY_TIME  <=   modifyTimeTo}


    AND comSms.CREATE_TIME  >=   createTimeFrom}


    AND comSms.CREATE_TIME  <=   createTimeTo}

select  *  from com_sms
where  IS_DELETEMARK = 1
 and id =#{ comSms.id} 
 and applyDateStr = #{ comSms.applyDateStr} 
 and areaType =#{ comSms.areaType} 
 and typeno =#{ comSms.typeno} 
 and sendcode = #{ comSms.sendcode} 
 and sendname = #{ comSms.sendname} 
 and mobile = #{ comSms.mobile} 
 and sendType =#{ comSms.sendType} 
 and refTableId =#{ comSms.refTableId} 
 and operatorName = #{ comSms.operatorName} 
 and sendcontent like concat('%', #{ comSms.sendcontent} ,'%')
 and COMMENTS like concat('%', #{ comSms.comments} ,'%')
 and STATE =#{ comSms.state} 

    AND comSms.MODIFY_TIME  >=   modifyTimeFrom}


    AND comSms.MODIFY_TIME  <=   modifyTimeTo}


    AND comSms.CREATE_TIME  >=   createTimeFrom}


    AND comSms.CREATE_TIME  <=   createTimeTo}

order by CREATE_TIME desc limit 150

SELECT
     *
 FROM
     com_sms
 WHERE
     IS_DELETEMARK = 1
      and id =#{ comSms.id} 
      and sendcode = #{ comSms.sendcode} 
      and applyDateStr =#{ comSms.applyDateStr} 
      and sendType =#{ comSms.sendType} 
      and areaType =#{ comSms.areaType} 
      and typeno =#{ comSms.typeno} 
      and STATE =#{ comSms.state} 
      and mobile = #{ comSms.mobile} 
      and refTableId =#{ comSms.refTableId} 
 ORDER BY
     CREATE_TIME desc
     LIMIT 1
