mapper.update:
update yb_drg_apply
applyDate=#{applyDate}, applyDateStr=#{applyDateStr}, operatorId=#{operatorId}, operatorName=#{operatorName}, uploadFileName=#{uploadFileName}, endDate=#{endDate}, taskState=#{taskState}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, areaType=#{areaType}, enableDate=#{enableDate},
where id=#{id}

mapper.select:
select  *  from yb_drg_apply
where  1=1
     and id =#{ ybDrgApply.id} 

    AND ybDrgApply.applyDate  >=   applyDateFrom}


    AND ybDrgApply.applyDate  <=   applyDateTo}

     and applyDateStr like concat('%', #{ ybDrgApply.applyDateStr} ,'%')
     and operatorName like concat('%', #{ ybDrgApply.operatorName} ,'%')
     and uploadFileName like concat('%', #{ ybDrgApply.uploadFileName} ,'%')

    AND ybDrgApply.endDate  >=   endDateFrom}


    AND ybDrgApply.endDate  <=   endDateTo}

 and taskState =#{ ybDrgApply.taskState} 
     and COMMENTS like concat('%', #{ ybDrgApply.comments} ,'%')
 and STATE =#{ ybDrgApply.state} 
 and IS_DELETEMARK =#{ ybDrgApply.isDeletemark} 

    AND ybDrgApply.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybDrgApply.MODIFY_TIME  <=   modifyTimeTo}


    AND ybDrgApply.CREATE_TIME  >=   createTimeFrom}


    AND ybDrgApply.CREATE_TIME  <=   createTimeTo}

 and areaType =#{ ybDrgApply.areaType} 

    AND ybDrgApply.enableDate  >=   enableDateFrom}


    AND ybDrgApply.enableDate  <=   enableDateTo}
