mapper.update:
update yb_drg_jk
applyDateStr=#{applyDateStr}, orderNumber=#{orderNumber}, orderNum=#{orderNum}, applyDataId=#{applyDataId}, areaType=#{areaType}, ryDate=#{ryDate}, cyDate=#{cyDate}, fzCode=#{fzCode}, fzName=#{fzName}, zyzdCode=#{zyzdCode}, zyzdName=#{zyzdName}, zssCode=#{zssCode}, zssName=#{zssName}, qtzdCode=#{qtzdCode}, qtzdName=#{qtzdName}, qtssCode=#{qtssCode}, qtssName=#{qtssName}, deptName=#{deptName}, areaId=#{areaId}, areaName=#{areaName}, qz=#{qz}, kzrDocId=#{kzrDocId}, kzrDocName=#{kzrDocName}, zrysDocId=#{zrysDocId}, zrysDocName=#{zrysDocName}, zzysDocId=#{zzysDocId}, zzysDocName=#{zzysDocName}, zyysDocId=#{zyysDocId}, zyysDocName=#{zyysDocName},
where id=#{id}

mapper.select:
select * from yb_drg_jk
where applyDateStr = #{ ybDrgJk.applyDateStr} and areaType =#{ ybDrgJk.areaType}
and applyDataId =#{ ybDrgJk.applyDataId} 

    AND ybDrgJk.ryDate  >=   ryDateFrom}


    AND ybDrgJk.ryDate  <=   ryDateTo}


    AND ybDrgJk.cyDate  >=   cyDateFrom}


    AND ybDrgJk.cyDate  <=   cyDateTo}

and fzCode like concat('%', #{ ybDrgJk.fzCode} ,'%')
and fzName like concat('%', #{ ybDrgJk.fzName} ,'%')
and zyzdCode like concat('%', #{ ybDrgJk.zyzdCode}
    ,'%')

and zyzdName like concat('%', #{ ybDrgJk.zyzdName}
    ,'%')

and zssCode like concat('%', #{ ybDrgJk.zssCode} ,'%')
and zssName like concat('%', #{ ybDrgJk.zssName} ,'%')
and qtzdCode like concat('%', #{ ybDrgJk.qtzdCode}
    ,'%')

and qtzdName like concat('%', #{ ybDrgJk.qtzdName}
    ,'%')

and qtssCode like concat('%', #{ ybDrgJk.qtssCode}
    ,'%')

and qtssName like concat('%', #{ ybDrgJk.qtssName}
    ,'%')

and deptName like concat('%', #{ ybDrgJk.deptName} ,'%')
and areaId like concat('%', #{ ybDrgJk.areaId} ,'%')
and areaName like concat('%', #{ ybDrgJk.areaName} ,'%')
and kzrDocId like concat('%', #{ ybDrgJk.kzrDocId} ,'%')
and kzrDocName like concat('%', #{ ybDrgJk.kzrDocName} ,'%')
and zrysDocId like concat('%', #{ ybDrgJk.zrysDocId}
    ,'%')

and zrysDocName like concat('%', #{ ybDrgJk.zrysDocName}
    ,'%')

and zzysDocId like concat('%', #{ ybDrgJk.zzysDocId}
    ,'%')

and zzysDocName like concat('%', #{ ybDrgJk.zzysDocName}
    ,'%')

and zyysDocId like concat('%', #{ ybDrgJk.zyysDocId}
    ,'%')

and zyysDocName like concat('%', #{ ybDrgJk.zyysDocName}
    ,'%')
