mapper.update:
update yb_drg_manage
applyDataId=#{applyDataId}, verifyId=#{verifyId}, readyDoctorCode=#{readyDoctorCode}, readyDoctorName=#{readyDoctorName}, readyDeptCode=#{readyDeptCode}, readyDeptName=#{readyDeptName}, changeDoctorCode=#{changeDoctorCode}, changeDoctorName=#{changeDoctorName}, changeDeptCode=#{changeDeptCode}, changeDeptName=#{changeDeptName}, operateReason=#{operateReason}, operateDate=#{operateDate}, refuseId=#{refuseId}, refuseName=#{refuseName}, refuseReason=#{refuseReason}, refuseDate=#{refuseDate}, adminPersonId=#{adminPersonId}, adminPersonName=#{adminPersonName}, adminChangeDate=#{adminChangeDate}, adminReason=#{adminReason}, STATE=#{state}, enableDate=#{enableDate}, operateProcess=#{operateProcess}, approvalState=#{approvalState}, applyDateStr=#{applyDateStr}, orderNumber=#{orderNumber}, orderNum=#{orderNum}, areaType=#{areaType},
where id=#{id}

mapper.select:
select  *  from yb_drg_manage
where  1=1
     and id =#{ ybDrgManage.id} 
     and applyDataId like concat('%', #{ ybDrgManage.applyDataId} ,'%')
     and verifyId like concat('%', #{ ybDrgManage.verifyId} ,'%')
     and readyDoctorCode like concat('%', #{ ybDrgManage.readyDoctorCode} ,'%')
     and readyDoctorName like concat('%', #{ ybDrgManage.readyDoctorName} ,'%')
     and readyDeptCode like concat('%', #{ ybDrgManage.readyDeptCode} ,'%')
     and readyDeptName like concat('%', #{ ybDrgManage.readyDeptName} ,'%')
     and changeDoctorCode like concat('%', #{ ybDrgManage.changeDoctorCode} ,'%')
     and changeDoctorName like concat('%', #{ ybDrgManage.changeDoctorName} ,'%')
     and changeDeptCode like concat('%', #{ ybDrgManage.changeDeptCode} ,'%')
     and changeDeptName like concat('%', #{ ybDrgManage.changeDeptName} ,'%')
     and operateReason like concat('%', #{ ybDrgManage.operateReason} ,'%')

    AND ybDrgManage.operateDate  >=   operateDateFrom}


    AND ybDrgManage.operateDate  <=   operateDateTo}

     and refuseName like concat('%', #{ ybDrgManage.refuseName} ,'%')
     and refuseReason like concat('%', #{ ybDrgManage.refuseReason} ,'%')

    AND ybDrgManage.refuseDate  >=   refuseDateFrom}


    AND ybDrgManage.refuseDate  <=   refuseDateTo}

     and adminPersonName like concat('%', #{ ybDrgManage.adminPersonName} ,'%')

    AND ybDrgManage.adminChangeDate  >=   adminChangeDateFrom}


    AND ybDrgManage.adminChangeDate  <=   adminChangeDateTo}

     and adminReason like concat('%', #{ ybDrgManage.adminReason} ,'%')
 and STATE =#{ ybDrgManage.state} 

    AND ybDrgManage.enableDate  >=   enableDateFrom}


    AND ybDrgManage.enableDate  <=   enableDateTo}

     and operateProcess like concat('%', #{ ybDrgManage.operateProcess} ,'%')
 and approvalState =#{ ybDrgManage.approvalState} 
     and applyDateStr like concat('%', #{ ybDrgManage.applyDateStr} ,'%')
     and orderNumber like concat('%', #{ ybDrgManage.orderNumber} ,'%')
 and orderNum =#{ ybDrgManage.orderNum} 
 and areaType =#{ ybDrgManage.areaType}
