mapper.update:
update yb_drg_verify
applyDataId=#{applyDataId}, verifyDoctorCode=#{verifyDoctorCode}, verifyDoctorName=#{verifyDoctorName}, verifyDeptCode=#{verifyDeptCode}, verifyDeptName=#{verifyDeptName}, STATE=#{state}, applyDateStr=#{applyDateStr}, orderNumber=#{orderNumber}, orderNum=#{orderNum}, areaType=#{areaType}, sendPersonId=#{sendPersonId}, sendPersonName=#{sendPersonName}, sendDate=#{sendDate},
where id=#{id}

mapper.select:
select  *  from yb_drg_verify
where  1=1
     and id =#{ ybDrgVerify.id} 
     and applyDataId like concat('%', #{ ybDrgVerify.applyDataId} ,'%')
     and verifyDoctorCode like concat('%', #{ ybDrgVerify.verifyDoctorCode} ,'%')
     and verifyDoctorName like concat('%', #{ ybDrgVerify.verifyDoctorName} ,'%')
     and verifyDeptCode like concat('%', #{ ybDrgVerify.verifyDeptCode} ,'%')
     and verifyDeptName like concat('%', #{ ybDrgVerify.verifyDeptName} ,'%')
 and STATE =#{ ybDrgVerify.state} 
     and applyDateStr like concat('%', #{ ybDrgVerify.applyDateStr} ,'%')
     and orderNumber like concat('%', #{ ybDrgVerify.orderNumber} ,'%')
 and orderNum =#{ ybDrgVerify.orderNum} 
 and areaType =#{ ybDrgVerify.areaType} 
     and sendPersonName like concat('%', #{ ybDrgVerify.sendPersonName} ,'%')

    AND ybDrgVerify.sendDate  >=   sendDateFrom}


    AND ybDrgVerify.sendDate  <=   sendDateTo}

SELECT
*
FROM
yb_drg_verify
WHERE
applyDateStr = #{ applyDateStr }
 and areaType =#{ areaType } 
 and state =#{ state }
