mapper.select:
select case when rv.id is null then UUID() else rv.id end id, ad.pid, ad.id applyDataId, #{ ybDrgVerifyView.applyDateStr} applyDateStr, ad.ks, ad.jzjlh, ad.bah, ad.wglx, ad.wtms, ad.ylzfy, ad.wgje, ad.sfbmzczjcw, ad.ly, ad.orderNumber, ad.orderNum, rv.verifyDoctorCode, concat(rv.verifyDoctorCode,'-',rv.verifyDoctorName) verifyDoctorName, rv.verifyDeptCode, concat(rv.verifyDeptCode,'-',rv.verifyDeptName) verifyDeptName, case when rv.id is null then 0 else 1 end isVerify, case when yp.id is null then 0 else 1 end isPerson, rv.sendPersonId, rv.sendPersonName, rv.sendDate, rv.STATE, rv.areaType from yb_drg_verify rv INNER JOIN yb_drg_apply_data ad ON ad.id = rv.applyDataId and ad.pid = #{ ybDrgVerifyView.pid}
and ad.orderNumber = #{ ybDrgVerifyView.orderNumber}
and ad.ks = #{ ybDrgVerifyView.ks}
and ad.jzjlh = #{ ybDrgVerifyView.jzjlh}
and ad.bah = #{ ybDrgVerifyView.bah}
LEFT join yb_person yp on rv.verifyDoctorCode = yp.personCode where rv.applyDateStr = #{ ybDrgVerifyView.applyDateStr} and rv.areaType = #{ ybDrgVerifyView.areaType} and rv.state =#{ ybDrgVerifyView.state}
and rv.orderNumber = #{ ybDrgVerifyView.orderNumber}
and rv.verifyDoctorCode = #{ ybDrgVerifyView.verifyDoctorCode}
and rv.verifyDoctorName = #{ ybDrgVerifyView.verifyDoctorName}
and rv.verifyDeptName = #{ ybDrgVerifyView.verifyDeptName}
order by rv.orderNum

select
count(1)
from
yb_drg_verify rv
inner JOIN yb_drg_apply_data ad ON
ad.id = rv.applyDataId and
ad.pid =  #{ ybDrgVerifyView.pid}

    and ad.orderNumber = #{ ybDrgVerifyView.orderNumber}


    and ad.ks = #{ ybDrgVerifyView.ks}


    and ad.jzjlh = #{ ybDrgVerifyView.jzjlh}


    and ad.bah = #{ ybDrgVerifyView.bah}

where
rv.applyDateStr = #{ ybDrgVerifyView.applyDateStr}
and rv.areaType = #{ ybDrgVerifyView.areaType}
and rv.state =#{ ybDrgVerifyView.state}

    and rv.orderNumber = #{ ybDrgVerifyView.orderNumber}


    and rv.verifyDoctorCode = #{ ybDrgVerifyView.verifyDoctorCode}


    and rv.verifyDoctorName = #{ ybDrgVerifyView.verifyDoctorName}


    and rv.verifyDeptName = #{ ybDrgVerifyView.verifyDeptName}
