mapper.update:
update yb_appeal_confire
doctorCode=#{doctorCode}, doctorName=#{doctorName}, adminType=#{adminType}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, currencyField=#{currencyField}, areaType=#{areaType},
where id=#{id}

mapper.select:
select  *  from yb_appeal_confire
where  1=1
     and id =#{ ybAppealConfire.id} 
     and doctorCode like concat('%', #{ ybAppealConfire.doctorCode} ,'%')
     and doctorName like concat('%', #{ ybAppealConfire.doctorName} ,'%')
 and adminType =#{ ybAppealConfire.adminType} 
     and COMMENTS like concat('%', #{ ybAppealConfire.comments} ,'%')
 and STATE =#{ ybAppealConfire.state} 
 and IS_DELETEMARK =#{ ybAppealConfire.isDeletemark} 

    AND ybAppealConfire.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybAppealConfire.MODIFY_TIME  <=   modifyTimeTo}


    AND ybAppealConfire.CREATE_TIME  >=   createTimeFrom}


    AND ybAppealConfire.CREATE_TIME  <=   createTimeTo}

     and currencyField like concat('%', #{ ybAppealConfire.currencyField} ,'%')
and ybAppealConfire.areaType =#{ ybAppealConfire.areaType}

SELECT p.id, p.doctorCode, p.doctorName, p.adminType, p.COMMENTS, p.STATE, p.IS_DELETEMARK, p.MODIFY_TIME, p.CREATE_TIME, p.CREATE_USER_ID, p.MODIFY_USER_ID, GROUP_CONCAT(CONCAT(d.deptId,'-',d.deptName)) currencyField, p.areaType FROM yb_appeal_confire p left join yb_appeal_confire_data d on p.id = d.pid where p.IS_DELETEMARK = 1
AND p.areaType = #{ areaType }
and (p.doctorCode like concat('%', #{ doctorContent } ,'%') or p.doctorName like concat('%', #{ doctorContent } ,'%'))
and p.adminType = #{ adminType }
and p.id in(select DISTINCT pid from yb_appeal_confire_data where deptName like concat('%', #{ deptContent } ,'%'))
GROUP BY p.id order by p.create_time desc

SELECT count(1) FROM yb_appeal_confire where IS_DELETEMARK = 1
AND areaType = #{ areaType }
and adminType = #{ adminType }
and (doctorCode like concat('%', #{ doctorContent } ,'%') or doctorName like concat('%', #{ doctorContent } ,'%'))
and id in(select DISTINCT pid from yb_appeal_confire_data where deptName like concat('%', #{ deptContent } ,'%'))

SELECT * FROM yb_appeal_confire where doctorCode= #{ ybAppealConfire.doctorCode }

    and id !=  #{ ybAppealConfire.id }


    and areaType =  #{ ybAppealConfire.areaType }

and adminType in (
    select id from com_type where ctName like concat('%', #{ ybAppealConfire.comments } ,'%')
)

SELECT count(1) FROM yb_appeal_confire where IS_DELETEMARK = 1
AND areaType = #{ areaType }
and adminType = #{ adminType }
and CREATE_USER_ID = #{ uid }
and (CREATE_USER_ID = #{ uid } or doctorCode=#{ doctorCode })
and (doctorCode like concat('%', #{ doctorContent } ,'%') or doctorName like concat('%', #{ doctorContent } ,'%'))
and id in(select DISTINCT pid from yb_appeal_confire_data where deptName like concat('%', #{ deptContent } ,'%'))
