mapper.update:
update yb_appeal_manage
applyDataId=#{applyDataId}, verifyId=#{verifyId}, readyDoctorCode=#{readyDoctorCode}, readyDoctorName=#{readyDoctorName}, readyDeptCode=#{readyDeptCode}, readyDeptName=#{readyDeptName}, changeDoctorCode=#{changeDoctorCode}, changeDoctorName=#{changeDoctorName}, changeDeptCode=#{changeDeptCode}, changeDeptName=#{changeDeptName}, acceptState=#{acceptState}, operateReason=#{operateReason}, operateDate=#{operateDate}, refuseId=#{refuseId}, refuseName=#{refuseName}, refuseReason=#{refuseReason}, refuseDate=#{refuseDate}, adminPersonId=#{adminPersonId}, adminPersonName=#{adminPersonName}, adminChangeDate=#{adminChangeDate}, adminReason=#{adminReason}, enableDate=#{enableDate}, currencyField=#{currencyField}, sourceType=#{sourceType}, dataType=#{dataType}, orderDoctorCode=#{orderDoctorCode}, orderDoctorName=#{orderDoctorName}, orderDeptCode=#{orderDeptCode}, orderDeptName=#{orderDeptName}, approvalState=#{approvalState}, applyDateStr=#{applyDateStr}, typeno=#{typeno}, orderNumber=#{orderNumber}, orderNum=#{orderNum}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, areaType=#{areaType},
where id=#{id}

mapper.select:
select  *  from yb_appeal_manage
where  1=1
 and id =#{ ybAppealManage.id} 
 and applyDataId =#{ ybAppealManage.applyDataId} 
 and verifyId =#{ ybAppealManage.verifyId} 
     and readyDoctorCode like concat('%', #{ ybAppealManage.readyDoctorCode} ,'%')
     and readyDoctorName like concat('%', #{ ybAppealManage.readyDoctorName} ,'%')
     and readyDeptCode like concat('%', #{ ybAppealManage.readyDeptCode} ,'%')
     and readyDeptName like concat('%', #{ ybAppealManage.readyDeptName} ,'%')
     and changeDoctorCode like concat('%', #{ ybAppealManage.changeDoctorCode} ,'%')
     and changeDoctorName like concat('%', #{ ybAppealManage.changeDoctorName} ,'%')
     and changeDeptCode like concat('%', #{ ybAppealManage.changeDeptCode} ,'%')
     and changeDeptName like concat('%', #{ ybAppealManage.changeDeptName} ,'%')
 and acceptState =#{ ybAppealManage.acceptState} 
     and operateReason like concat('%', #{ ybAppealManage.operateReason} ,'%')

    AND ybAppealManage.operateDate  >=   operateDateFrom}


    AND ybAppealManage.operateDate  <=   operateDateTo}

     and refuseName like concat('%', #{ ybAppealManage.refuseName} ,'%')
     and refuseReason like concat('%', #{ ybAppealManage.refuseReason} ,'%')

    AND ybAppealManage.refuseDate  >=   refuseDateFrom}


    AND ybAppealManage.refuseDate  <=   refuseDateTo}

     and adminPersonName like concat('%', #{ ybAppealManage.adminPersonName} ,'%')

    AND ybAppealManage.adminChangeDate  >=   adminChangeDateFrom}


    AND ybAppealManage.adminChangeDate  <=   adminChangeDateTo}

     and adminReason like concat('%', #{ ybAppealManage.adminReason} ,'%')

    AND ybAppealManage.enableDate  >=   enableDateFrom}


    AND ybAppealManage.enableDate  <=   enableDateTo}

     and currencyField like concat('%', #{ ybAppealManage.currencyField} ,'%')
 and sourceType =#{ ybAppealManage.sourceType} 
     and COMMENTS like concat('%', #{ ybAppealManage.comments} ,'%')
 and STATE =#{ ybAppealManage.state} 
 and approvalState =#{ ybAppealManage.approvalState} 
 and IS_DELETEMARK =#{ ybAppealManage.isDeletemark} 

    AND ybAppealManage.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybAppealManage.MODIFY_TIME  <=   modifyTimeTo}


    AND ybAppealManage.CREATE_TIME  >=   createTimeFrom}


    AND ybAppealManage.CREATE_TIME  <=   createTimeTo}
