mapper.select:
select ad.pid, ad.serialNo, ad.billNo, ad.proposalCode, ad.projectCode, ad.projectName, ad.num, ad.medicalPrice, ad.ruleName, ad.deductPrice, ad.deductReason, ad.repaymentReason, ad.doctorName, ad.deptCode, ad.deptName, ad.enterHospitalDateStr, ad.outHospitalDateStr, ad.costDateStr, ad.hospitalizedNo, ad.treatmentMode, ad.settlementDateStr, ad.personalNo, ad.insuredName, ad.cardNumber, ad.areaName, ad.versionNumber, ad.backAppeal, am.typeno, ad.insuredType, am.dataType, ad.orderNumber, ad.orderNum, ra.applyDateStr, case when am.sourceType = 1 then ra.endDateReset else CASE WHEN am.typeno=1 THEN ra.endDateOne ELSE ra.endDateTwo END end applyEndDate, am.id, ad.id applyDataId, am.verifyId, am.readyDoctorCode, am.readyDoctorName, am.readyDeptCode, am.readyDeptName, am.changeDoctorCode, am.changeDoctorName, am.changeDeptCode, am.changeDeptName, am.operateReason, am.operateDate, am.acceptState, am.refuseId, am.refuseName, am.refuseReason, am.refuseDate, am.adminPersonId, am.adminPersonName, am.adminChangeDate, am.adminReason, CASE WHEN am.typeno = 1 THEN CASE WHEN DATE_FORMAT(am.enableDate,'%Y-%m-%d %H:%i:%s')>DATE_FORMAT(ra.endDateOne,'%Y-%m-%d %H:%i:%s' ) = 1 THEN 0 ELSE 1 END ELSE CASE WHEN DATE_FORMAT(am.enableDate,'%Y-%m-%d %H:%i:%s')>DATE_FORMAT(ra.endDateTwo,'%Y-%m-%d %H:%i:%s' ) = 1 THEN 0 ELSE 1 END END isEnableDate, date_add(am.enableDate,INTERVAL -1 DAY) enableDate, am.STATE, '' currencyField, am.sourceType, am.operateProcess, am.verifySendId, am.approvalState, am.orderDoctorCode, am.orderDoctorName, am.orderDeptCode, am.orderDeptName, CASE WHEN am.sourceType = 0 THEN CASE WHEN am.typeno = 1 THEN case when (UNIX_TIMESTAMP(ra.endDateOne)-UNIX_TIMESTAMP(sysdate()))/(60*60*24) > 0 then 0 else 1 end ELSE case when (UNIX_TIMESTAMP(ra.endDateTwo)-UNIX_TIMESTAMP(sysdate()))/(60*60*24) > 0 then 0 else 1 end END ELSE case when (UNIX_TIMESTAMP(ra.endDateReset)-UNIX_TIMESTAMP(sysdate()))/(60*60*24) > 0 then 0 else 1 end END isEnd, case when (UNIX_TIMESTAMP(am.enableDate)-UNIX_TIMESTAMP(sysdate()))/(60*60*24) > 0 then 1 else 0 end enableType, am.areaType, am.dksName from yb_appeal_manage am STRAIGHT_JOIN yb_reconsider_apply_data ad ON am.applyDataId = ad.id and ad.pid = #{ybAppealManageView.pid} STRAIGHT_JOIN yb_reconsider_apply ra ON ra.id = ad.pid and ra.id = #{ybAppealManageView.pid} where am.applyDateStr = #{ybAppealManageView.applyDateStr}
and am.areaType = #{ybAppealManageView.areaType}
and am.readyDoctorCode = #{ ybAppealManageView.readyDoctorCode}
and am.readyDoctorCode in (select personCode from yb_person where personName = #{ ybAppealManageView.readyDoctorName})
and am.orderDoctorCode = #{ybAppealManageView.orderDoctorCode}
and am.readyDeptName = #{ybAppealManageView.readyDeptName}
and (am.acceptState = 1 or am.acceptState = 0)
and (am.acceptState = 1 or am.acceptState = 0 or am.acceptState = 2)
and am.acceptState = #{ybAppealManageView.acceptState}
and am.sourceType = 1
and am.sourceType = 0 and am.typeno =#{ ybAppealManageView.typeno}
and am.dksName in(SELECT dksName FROM yb_appeal_confire_data where pid = #{ appealConfireId})
order by am.typeno,am.dataType,am.orderNum asc

select
count(1)
from
yb_appeal_manage am
STRAIGHT_JOIN yb_reconsider_apply_data ad ON
am.applyDataId = ad.id and ad.pid = #{ybAppealManageView.pid}
where
am.applyDateStr = #{ybAppealManageView.applyDateStr}

    and am.areaType = #{ybAppealManageView.areaType}

    and am.readyDoctorCode = #{ ybAppealManageView.readyDoctorCode}

    and am.readyDoctorCode in (select personCode from yb_person where personName = #{ ybAppealManageView.readyDoctorName})

    and am.orderDoctorCode = #{ybAppealManageView.orderDoctorCode}

    and am.readyDeptName = #{ybAppealManageView.readyDeptName}

    and (am.acceptState = 1 or am.acceptState = 0)

    and (am.acceptState = 1 or am.acceptState = 0 or am.acceptState = 2)

    and am.acceptState = #{ybAppealManageView.acceptState}

    and am.sourceType = 1

    and am.sourceType = 0 and am.typeno =#{ ybAppealManageView.typeno}

    and am.dksName in(SELECT dksName FROM yb_appeal_confire_data where pid = #{ appealConfireId})

select ad.pid, ad.serialNo, ad.billNo, ad.proposalCode, ad.projectCode, ad.projectName, ad.num, ad.medicalPrice, ad.ruleName, ad.deductPrice, ad.deductReason, ad.repaymentReason, ad.doctorName, ad.deptCode, ad.deptName, ad.enterHospitalDateStr, ad.outHospitalDateStr, ad.costDateStr, ad.hospitalizedNo, ad.treatmentMode, ad.settlementDateStr, ad.personalNo, ad.insuredName, ad.cardNumber, ad.areaName, ad.versionNumber, ad.backAppeal, am.typeno, ad.insuredType, am.dataType, ad.orderNumber, ad.orderNum, ra.applyDateStr, case when am.sourceType = 1 then ra.endDateReset else CASE WHEN am.typeno=1 THEN ra.endDateOne ELSE ra.endDateTwo END end applyEndDate, am.id, ad.id applyDataId, am.verifyId, am.readyDoctorCode, am.readyDoctorName, am.readyDeptCode, am.readyDeptName, am.changeDoctorCode, am.changeDoctorName, am.changeDeptCode, am.changeDeptName, am.operateReason, am.operateDate, am.acceptState, am.refuseId, am.refuseName, am.refuseReason, am.refuseDate, am.adminPersonId, am.adminPersonName, am.adminChangeDate, am.adminReason, CASE WHEN am.typeno = 1 THEN CASE WHEN DATE_FORMAT(am.enableDate,'%Y-%m-%d %H:%i:%s')>DATE_FORMAT(ra.endDateOne,'%Y-%m-%d %H:%i:%s' ) = 1 THEN 0 ELSE 1 END ELSE CASE WHEN DATE_FORMAT(am.enableDate,'%Y-%m-%d %H:%i:%s')>DATE_FORMAT(ra.endDateTwo,'%Y-%m-%d %H:%i:%s' ) = 1 THEN 0 ELSE 1 END END isEnableDate, date_add(am.enableDate,INTERVAL -1 DAY) enableDate, am.STATE, '' currencyField, am.sourceType, am.operateProcess, am.verifySendId, am.approvalState, am.orderDoctorCode, am.orderDoctorName, am.orderDeptCode, am.orderDeptName, CASE WHEN am.sourceType = 0 THEN CASE WHEN am.typeno = 1 THEN case when (UNIX_TIMESTAMP(ra.endDateOne)-UNIX_TIMESTAMP(sysdate()))/(60*60*24) > 0 then 0 else 1 end ELSE case when (UNIX_TIMESTAMP(ra.endDateTwo)-UNIX_TIMESTAMP(sysdate()))/(60*60*24) > 0 then 0 else 1 end END ELSE case when (UNIX_TIMESTAMP(ra.endDateReset)-UNIX_TIMESTAMP(sysdate()))/(60*60*24) > 0 then 0 else 1 end END isEnd, case when (UNIX_TIMESTAMP(am.enableDate)-UNIX_TIMESTAMP(sysdate()))/(60*60*24) > 0 then 1 else 0 end enableType, am.areaType, am.dksName from yb_reconsider_apply_data ad inner join yb_appeal_manage am ON am.applyDataId = ad.id and am.applyDateStr = #{ybAppealManageView.applyDateStr}
and am.areaType = #{ybAppealManageView.areaType}
and am.readyDoctorCode = #{ ybAppealManageView.readyDoctorCode}
and am.orderDoctorCode = #{ybAppealManageView.orderDoctorCode}
and am.readyDeptName = #{ybAppealManageView.readyDeptName}
and (am.acceptState = 1 or am.acceptState = 0)
and (am.acceptState = 1 or am.acceptState = 0 or am.acceptState = 2)
and am.acceptState = #{ybAppealManageView.acceptState}
and am.sourceType = 1
and am.sourceType = 0 and am.typeno =#{ ybAppealManageView.typeno}
and am.dksName in(SELECT dksName FROM yb_appeal_confire_data where pid = #{ appealConfireId})
inner join yb_reconsider_apply ra ON ra.id = ad.pid where ad.pid = #{ ybAppealManageView.pid}
and ad.typeno =#{ ybAppealManageView.typeno}
and ad.serialNo = #{ ybAppealManageView.currencyField}
and ad.proposalCode = #{ ybAppealManageView.currencyField}
and ad.projectCode = #{ ybAppealManageView.currencyField}
and ad.projectName = #{ ybAppealManageView.currencyField}
and ad.ruleName = #{ ybAppealManageView.currencyField}
and ad.orderNumber = #{ ybAppealManageView.currencyField}

select
count(1)
from
yb_reconsider_apply_data ad
inner join yb_appeal_manage am  ON
am.applyDataId = ad.id and
am.applyDateStr = #{ybAppealManageView.applyDateStr}

    and am.areaType = #{ybAppealManageView.areaType}


    and am.readyDoctorCode = #{ ybAppealManageView.readyDoctorCode}


    and am.orderDoctorCode = #{ybAppealManageView.orderDoctorCode}


    and am.readyDeptName = #{ybAppealManageView.readyDeptName}


    and (am.acceptState = 1 or am.acceptState = 0)


    and (am.acceptState = 1 or am.acceptState = 0 or am.acceptState = 2)


    and am.acceptState = #{ybAppealManageView.acceptState}


    and am.sourceType = 1


    and am.sourceType = 0 and am.typeno =#{ ybAppealManageView.typeno}


    and am.dksName in(SELECT dksName FROM yb_appeal_confire_data where pid = #{ appealConfireId})

where
ad.pid = #{ ybAppealManageView.pid}

    and ad.typeno =#{ ybAppealManageView.typeno}


    and ad.serialNo = #{ ybAppealManageView.currencyField}


