mapper.select:
select ad.pid, ad.serialNo, ad.billNo, ad.proposalCode, ad.projectCode, ad.projectName, ad.num, ad.medicalPrice, ad.ruleName, ad.deductPrice, ad.deductReason, ad.repaymentReason, ad.doctorName, ad.deptCode, ad.deptName, ad.enterHospitalDateStr, ad.outHospitalDateStr, ad.costDateStr, ad.hospitalizedNo, ad.treatmentMode, ad.settlementDateStr, ad.personalNo, ad.insuredName, ad.cardNumber, ad.areaName, ad.versionNumber, ad.backAppeal, am.typeno, ad.insuredType, am.dataType, ad.orderNumber, ad.orderNum, ra.applyDateStr, CASE WHEN am.typeno=1 THEN ra.endDateOne ELSE ra.endDateTwo END applyEndDate, am.id, ad.id applyDataId, am.verifyId, am.readyDoctorCode, am.readyDoctorName, am.readyDeptCode, am.readyDeptName, am.changeDoctorCode, am.changeDoctorName, am.changeDeptCode, am.changeDeptName, am.operateReason, am.operateDate, am.acceptState, am.refuseId, am.refuseName, am.refuseReason, am.refuseDate, am.adminPersonId, am.adminPersonName, am.adminChangeDate, am.adminReason, CASE WHEN am.typeno = 1 THEN CASE WHEN DATE_FORMAT(am.enableDate,'%Y-%m-%d %H:%i:%s')>DATE_FORMAT(ra.endDateOne,'%Y-%m-%d %H:%i:%s' ) = 1 THEN 0 ELSE 1 END ELSE CASE WHEN DATE_FORMAT(am.enableDate,'%Y-%m-%d %H:%i:%s')>DATE_FORMAT(ra.endDateTwo,'%Y-%m-%d %H:%i:%s' ) = 1 THEN 0 ELSE 1 END END isEnableDate, date_add(am.enableDate,INTERVAL -1 DAY) enableDate, am.STATE, am.currencyField, am.sourceType, am.operateProcess, am.verifySendId, am.approvalState, am.orderDoctorCode, am.orderDoctorName, am.orderDeptCode, am.orderDeptName, CASE WHEN am.sourceType = 0 THEN CASE WHEN am.typeno = 1 THEN case when (UNIX_TIMESTAMP(ra.endDateOne)-UNIX_TIMESTAMP(sysdate()))/(60*60*24) > 0 then 0 else 1 end ELSE case when (UNIX_TIMESTAMP(ra.endDateTwo)-UNIX_TIMESTAMP(sysdate()))/(60*60*24) > 0 then 0 else 1 end END ELSE 0 END isEnd, case when (UNIX_TIMESTAMP(am.enableDate)-UNIX_TIMESTAMP(sysdate()))/(60*60*24) > 0 then 1 else 0 end enableType from yb_appeal_manage am STRAIGHT_JOIN yb_reconsider_apply_data ad ON am.applyDataId = ad.id and ad.pid = #{ybAppealManageView.pid} STRAIGHT_JOIN yb_reconsider_apply ra ON ra.id = ad.pid and ra.id = #{ybAppealManageView.pid} where am.applyDateStr = #{ybAppealManageView.applyDateStr}
and am.readyDoctorCode = #{ybAppealManageView.readyDoctorCode}
and am.readyDeptName = #{ybAppealManageView.readyDeptName}
and (am.acceptState = 1 or am.acceptState = 0)
and (am.acceptState = 1 or am.acceptState = 0 or am.acceptState = 2)
and am.acceptState = #{ybAppealManageView.acceptState}
and am.sourceType = 1
and am.typeno =#{ ybAppealManageView.typeno}
order by am.typeno,am.dataType,am.orderNum asc

select
count(1)
from
yb_appeal_manage am
STRAIGHT_JOIN yb_reconsider_apply_data ad ON
am.applyDataId = ad.id and ad.pid = #{ybAppealManageView.pid}
where
am.applyDateStr = #{ybAppealManageView.applyDateStr}

    and am.readyDoctorCode = #{ybAppealManageView.readyDoctorCode}


    and am.readyDeptName = #{ybAppealManageView.readyDeptName}


    and (am.acceptState = 1 or am.acceptState = 0)


    and (am.acceptState = 1 or am.acceptState = 0 or am.acceptState = 2)


    and am.acceptState = #{ybAppealManageView.acceptState}


    and am.sourceType = 1


    and am.typeno =#{ ybAppealManageView.typeno}

select ad.pid, ad.serialNo, ad.billNo, ad.proposalCode, ad.projectCode, ad.projectName, ad.num, ad.medicalPrice, ad.ruleName, ad.deductPrice, ad.deductReason, ad.repaymentReason, ad.doctorName, ad.deptCode, ad.deptName, ad.enterHospitalDateStr, ad.outHospitalDateStr, ad.costDateStr, ad.hospitalizedNo, ad.treatmentMode, ad.settlementDateStr, ad.personalNo, ad.insuredName, ad.cardNumber, ad.areaName, ad.versionNumber, ad.backAppeal, am.typeno, ad.insuredType, am.dataType, ad.orderNumber, ad.orderNum, ra.applyDateStr, CASE WHEN am.typeno=1 THEN ra.endDateOne ELSE ra.endDateTwo END applyEndDate, am.id, ad.id applyDataId, am.verifyId, am.readyDoctorCode, am.readyDoctorName, am.readyDeptCode, am.readyDeptName, am.changeDoctorCode, am.changeDoctorName, am.changeDeptCode, am.changeDeptName, am.operateReason, am.operateDate, am.acceptState, am.refuseId, am.refuseName, am.refuseReason, am.refuseDate, am.adminPersonId, am.adminPersonName, am.adminChangeDate, am.adminReason, CASE WHEN am.typeno = 1 THEN CASE WHEN DATE_FORMAT(am.enableDate,'%Y-%m-%d %H:%i:%s')>DATE_FORMAT(ra.endDateOne,'%Y-%m-%d %H:%i:%s' ) = 1 THEN 0 ELSE 1 END ELSE CASE WHEN DATE_FORMAT(am.enableDate,'%Y-%m-%d %H:%i:%s')>DATE_FORMAT(ra.endDateTwo,'%Y-%m-%d %H:%i:%s' ) = 1 THEN 0 ELSE 1 END END isEnableDate, date_add(am.enableDate,INTERVAL -1 DAY) enableDate, am.STATE, am.currencyField, am.sourceType, am.operateProcess, am.verifySendId, am.approvalState, am.orderDoctorCode, am.orderDoctorName, am.orderDeptCode, am.orderDeptName, CASE WHEN am.sourceType = 0 THEN CASE WHEN am.typeno = 1 THEN case when (UNIX_TIMESTAMP(ra.endDateOne)-UNIX_TIMESTAMP(sysdate()))/(60*60*24) > 0 then 0 else 1 end ELSE case when (UNIX_TIMESTAMP(ra.endDateTwo)-UNIX_TIMESTAMP(sysdate()))/(60*60*24) > 0 then 0 else 1 end END ELSE 0 END isEnd, case when (UNIX_TIMESTAMP(am.enableDate)-UNIX_TIMESTAMP(sysdate()))/(60*60*24) > 0 then 1 else 0 end enableType from yb_reconsider_apply_data ad inner join yb_appeal_manage am ON am.applyDataId = ad.id and am.applyDateStr = #{ybAppealManageView.applyDateStr}
and am.readyDoctorCode = #{ybAppealManageView.readyDoctorCode}
and am.readyDeptName = #{ybAppealManageView.readyDeptName}
and (am.acceptState = 1 or am.acceptState = 0)
and (am.acceptState = 1 or am.acceptState = 0 or am.acceptState = 2)
and am.acceptState = #{ybAppealManageView.acceptState}
and am.sourceType = 1
and am.typeno =#{ ybAppealManageView.typeno}
inner join yb_reconsider_apply ra ON ra.id = ad.pid where ( ad.pid = #{ ybAppealManageView.pid}
and ad.typeno =#{ ybAppealManageView.typeno}
)
and (ad.serialNo like concat('%', #{ ybAppealManageView.currencyField} ,'%') or ad.billNo like concat('%', #{ ybAppealManageView.currencyField} ,'%') or ad.orderNumber = #{ ybAppealManageView.currencyField} or ad.proposalCode like concat('%', #{ ybAppealManageView.currencyField} ,'%') or ad.projectCode like concat('%', #{ ybAppealManageView.currencyField} ,'%') or ad.projectName like concat('%', #{ ybAppealManageView.currencyField} ,'%') or ad.ruleName like concat('%', #{ ybAppealManageView.currencyField} ,'%'))

select
count(1)
from
yb_reconsider_apply_data ad
inner join yb_appeal_manage am  ON
am.applyDataId = ad.id and
am.applyDateStr = #{ybAppealManageView.applyDateStr}

    and am.readyDoctorCode = #{ybAppealManageView.readyDoctorCode}


    and am.readyDeptName = #{ybAppealManageView.readyDeptName}


    and (am.acceptState = 1 or am.acceptState = 0)


    and (am.acceptState = 1 or am.acceptState = 0 or am.acceptState = 2)


    and am.acceptState = #{ybAppealManageView.acceptState}


    and am.sourceType = 1


    and am.typeno =#{ ybAppealManageView.typeno}

inner join yb_reconsider_apply ra ON ra.id = ad.pid
where
(
ad.pid = #{ ybAppealManageView.pid}

    and ad.typeno =#{ ybAppealManageView.typeno}

)

    and (ad.serialNo like concat('%', #{ ybAppealManageView.currencyField} ,'%')
    or ad.billNo like concat('%', #{ ybAppealManageView.currencyField} ,'%')
    or ad.orderNumber = #{ ybAppealManageView.currencyField}
    or ad.proposalCode like concat('%', #{ ybAppealManageView.currencyField} ,'%')
    or ad.projectCode like concat('%', #{ ybAppealManageView.currencyField} ,'%')
    or ad.projectName like concat('%', #{ ybAppealManageView.currencyField} ,'%')
    or ad.ruleName like concat('%', #{ ybAppealManageView.currencyField} ,'%'))

