d.id and ad.pid = #{ybAppealManageView.pid} STRAIGHT_JOIN yb_reconsider_apply ra ON ra.id = ad.pid and ra.id = #{ybAppealManageView.pid} where am.applyDateStr = #{ybAppealManageView.applyDateStr}
and am.acceptState = #{ybAppealManageView.acceptState}
