mapper.update:
update yb_appeal_result_deductimplement
resultId=#{resultId}, resetDataId=#{resetDataId}, implementDate=#{implementDate}, implementDateStr=#{implementDateStr}, shareState=#{shareState}, shareProgramme=#{shareProgramme}, applyDate=#{applyDate}, applyDateStr=#{applyDateStr}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select  *  from yb_appeal_result_deductimplement
where  1=1
     and id =#{ ybAppealResultDeductimplement.id} 
     and resultId like concat('%', #{ ybAppealResultDeductimplement.resultId} ,'%')
     and resetDataId like concat('%', #{ ybAppealResultDeductimplement.resetDataId} ,'%')

    AND ybAppealResultDeductimplement.implementDate  >=   implementDateFrom}


    AND ybAppealResultDeductimplement.implementDate  <=   implementDateTo}

     and implementDateStr like concat('%', #{ ybAppealResultDeductimplement.implementDateStr} ,'%')
 and shareState =#{ ybAppealResultDeductimplement.shareState} 
     and shareProgramme like concat('%', #{ ybAppealResultDeductimplement.shareProgramme} ,'%')

    AND ybAppealResultRepayment.applyDate  >=   applyDateFrom}


    AND ybAppealResultRepayment.applyDate  <=   applyDateTo}

and applyDateStr like
    concat('%', #{ ybAppealResultRepayment.applyDateStr} ,'%')

     and COMMENTS like concat('%', #{ ybAppealResultDeductimplement.comments} ,'%')
 and STATE =#{ ybAppealResultDeductimplement.state} 
 and IS_DELETEMARK =#{ ybAppealResultDeductimplement.isDeletemark} 

    AND ybAppealResultDeductimplement.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybAppealResultDeductimplement.MODIFY_TIME  <=   modifyTimeTo}


    AND ybAppealResultDeductimplement.CREATE_TIME  >=   createTimeFrom}


    AND ybAppealResultDeductimplement.CREATE_TIME  <=   createTimeTo}
