ardi.implementDateStr=#{ ybAppealResultDeductimplementView.implementDateStr}
ardi.implementDate >= #{ybAppealResultDeductimplementView.implementDateFrom}
and ardi.implementDate <= #{ybAppealResultDeductimplementView.implementDateTo}
and ardi.dataType=#{ ybAppealResultDeductimplementView.dataType}
and (rrd.serialNo like concat('%', #{ ybAppealResultDeductimplementView.currencyField} ,'%') or rrd.billNo like concat('%', #{ ybAppealResultDeductimplementView.currencyField} ,'%') or rrd.projectCode like concat('%', #{ ybAppealResultDeductimplementView.currencyField} ,'%') or rrd.projectName like concat('%', #{ ybAppealResultDeductimplementView.currencyField} ,'%') or rrd.ruleName like concat('%', #{ ybAppealResultDeductimplementView.currencyField} ,'%'))
