SELECT
count(1)
from
yb_reconsider_reset_data rrd
INNER JOIN yb_reconsider_reset rr on
rrd.pid = rr.id
INNER JOIN yb_appeal_result art ON
art.relatelDataId = rrd.relatelDataId AND
art.state = 2

    and art.dataType=#{ ybAppealResultDeductimplementView.dataType}


    and art.doctorCode=#{ ybAppealResultDeductimplementView.arDoctorCode}

where

    rr.id=#{ ybAppealResultDeductimplementView.pid}


    rr.applyDate  >=   #{ybAppealResultDeductimplementView.applyDateFrom}


    and rr.applyDate  <=   #{ybAppealResultDeductimplementView.applyDateTo}


    and rrd.dataType=#{ ybAppealResultDeductimplementView.dataType}

and rrd.id not in(
select ard.id from yb_appeal_result_deductimplement ard where

    ard.applyDateStr=#{ ybAppealResultDeductimplementView.applyDateStr}


    ard.applyDate  >=   #{ybAppealResultDeductimplementView.applyDateFrom}


    AND ard.applyDate  <=   #{ybAppealResultDeductimplementView.applyDateTo}


    and ard.dataType=#{ ybAppealResultDeductimplementView.dataType}

)

    and (rrd.serialNo like concat('%', #{ ybAppealResultDeductimplementView.currencyField} ,'%')
    or rrd.billNo like concat('%', #{ ybAppealResultDeductimplementView.currencyField} ,'%')
    or rrd.projectCode like concat('%', #{ ybAppealResultDeductimplementView.currencyField} ,'%')
    or rrd.projectName like concat('%', #{ ybAppealResultDeductimplementView.currencyField} ,'%')
    or rrd.ruleName like concat('%', #{ ybAppealResultDeductimplementView.currencyField} ,'%'))


    and (rrd.serialNo like concat('%', #{ ybAppealResultDeductimplementView.currencyField} ,'%')
    or rrd.billNo like concat('%', #{ ybAppealResultDeductimplementView.currencyField} ,'%')
    or rrd.ruleName like concat('%', #{ ybAppealResultDeductimplementView.currencyField} ,'%'))


    and (rrd.serialNo like concat('%', #{ ybAppealResultDeductimplementView.currencyField} ,'%')
    or rrd.billNo like concat('%', #{ ybAppealResultDeductimplementView.currencyField} ,'%')
    or rrd.projectCode like concat('%', #{ ybAppealResultDeductimplementView.currencyField} ,'%')
    or rrd.projectName like concat('%', #{ ybAppealResultDeductimplementView.currencyField} ,'%')
    or rrd.ruleName like concat('%', #{ ybAppealResultDeductimplementView.currencyField} ,'%'))
