SELECT rrd.pid, ardi.applyDate, ardi.applyDateStr, ardi.resetDataId, ardi.relatelDataId, ardi.implementDate, ardi.implementDateStr, ardi.shareState, ardi.shareProgramme, ardi.id deductImplementId, rrd.serialNo, rrd.billNo, rrd.projectCode, rrd.projectName, rrd.medicalPrice, rrd.ruleName, rrd.deductPrice, rrd.deductReason, rrd.repaymentReason, rrd.doctorName, rrd.deptCode, rrd.deptName, rrd.costDate, rrd.costDateStr, rrd.hospitalizedNo, rrd.treatmentMode, rrd.settlementDateStr, rrd.personalNo, rrd.insuredName, rrd.cardNumber, rrd.areaName, rrd.insuredType, rrd.dataType, rrd.orderNumber, rrd.orderNum, art.id, art.typeno, art.doctorCode arDoctorCode, art.doctorName arDoctorName, art.deptCode arDeptCode, art.deptName arDeptName, '' currencyField, art.areaType FROM yb_appeal_result_deductImplement ardi INNER JOIN yb_reconsider_reset_data rrd on ardi.resetDataId = rrd.id
and rrd.serialNo = #{ ybAppealResultDeductimplementView.currencyField}
and rrd.proposalCode = #{ ybAppealResultDeductimplementView.currencyField}
and rrd.projectCode = #{ ybAppealResultDeductimplementView.currencyField}
and rrd.projectName = #{ ybAppealResultDeductimplementView.currencyField}
and rrd.ruleName = #{ ybAppealResultDeductimplementView.currencyField}
and rrd.orderNumber = #{ ybAppealResultDeductimplementView.currencyField}
INNER JOIN yb_appeal_result art ON art.relatelDataId = rrd.relatelDataId
and art.doctorCode=#{ ybAppealResultDeductimplementView.arDoctorCode}
and art.doctorCode in (select personCode from yb_person where personName = #{ ybAppealResultDeductimplementView.arDoctorName})
where
ardi.implementDateStr=#{ ybAppealResultDeductimplementView.implementDateStr}
ardi.implementDate >= #{ybAppealResultDeductimplementView.implementDateFrom}
and ardi.implementDate <= #{ybAppealResultDeductimplementView.implementDateTo}
and ardi.areaType = #{ybAppealResultDeductimplementView.areaType}
and ardi.dataType=#{ ybAppealResultDeductimplementView.dataType}

SELECT
count(1)
FROM
yb_appeal_result_deductImplement ardi
INNER JOIN  yb_reconsider_reset_data rrd on
    ardi.resetDataId = rrd.id

    and rrd.serialNo = #{ ybAppealResultDeductimplementView.currencyField}


    and rrd.proposalCode = #{ ybAppealResultDeductimplementView.currencyField}


    and rrd.projectCode = #{ ybAppealResultDeductimplementView.currencyField}


    and rrd.projectName = #{ ybAppealResultDeductimplementView.currencyField}


    and rrd.ruleName = #{ ybAppealResultDeductimplementView.currencyField}


    and rrd.orderNumber = #{ ybAppealResultDeductimplementView.currencyField}

INNER JOIN yb_appeal_result art ON
    art.relatelDataId = rrd.relatelDataId

    and art.doctorCode=#{ ybAppealResultDeductimplementView.arDoctorCode}


    and art.doctorCode in (select personCode from yb_person where personName = #{ ybAppealResultDeductimplementView.arDoctorName})

where

    ardi.implementDateStr=#{ ybAppealResultDeductimplementView.implementDateStr}


    ardi.implementDate  >=   #{ybAppealResultDeductimplementView.implementDateFrom}


    and ardi.implementDate  <=   #{ybAppealResultDeductimplementView.implementDateTo}


    and ardi.areaType = #{ybAppealResultDeductimplementView.areaType}


    and ardi.dataType=#{ ybAppealResultDeductimplementView.dataType}

SELECT rrd.pid, art.applyDateStr, rrd.id resetDataId, rrd.relatelDataId, null implementDate, null implementDateStr, null shareState, null shareProgramme, null deductImplementId, rrd.serialNo, rrd.billNo, rrd.projectCode, rrd.projectName, rrd.medicalPrice, rrd.ruleName, rrd.deductPrice, rrd.deductReason, rrd.repaymentReason, rrd.doctorName, rrd.deptCode, rrd.deptName, rrd.costDate, rrd.costDateStr, rrd.hospitalizedNo, rrd.treatmentMode, rrd.settlementDateStr, rrd.personalNo, rrd.insuredName, rrd.cardNumber, rrd.areaName, rrd.insuredType, rrd.dataType, rrd.orderNumber, rrd.orderNum, art.id, art.typeno, art.doctorCode arDoctorCode, art.doctorName arDoctorName, art.deptCode arDeptCode, art.deptName arDeptName, '' currencyField, art.areaType from yb_reconsider_reset_data rrd INNER JOIN yb_appeal_result art ON art.relatelDataId = rrd.relatelDataId
and art.doctorCode=#{ ybAppealResultDeductimplementView.arDoctorCode}
and art.doctorCode in (select personCode from yb_person where personName = #{ ybAppealResultDeductimplementView.arDoctorName})
where
rrd.pid=#{ ybAppealResultDeductimplementView.pid}
and rrd.dataType=#{ ybAppealResultDeductimplementView.dataType}
and rrd.id not in( select ard.resetDataId from yb_appeal_result_deductimplement ard where ard.applyDateStr=#{ ybAppealResultDeductimplementView.applyDateStr}
and ard.areaType = #{ybAppealResultDeductimplementView.areaType}
and ard.dataType=#{ ybAppealResultDeductimplementView.dataType}
)
and rrd.serialNo = #{ ybAppealResultDeductimplementView.currencyField}
and rrd.proposalCode = #{ ybAppealResultDeductimplementView.currencyField}
and rrd.projectCode = #{ ybAppealResultDeductimplementView.currencyField}
and rrd.projectName = #{ ybAppealResultDeductimplementView.currencyField}
and rrd.ruleName = #{ ybAppealResultDeductimplementView.currencyField}
and rrd.orderNumber = #{ ybAppealResultDeductimplementView.currencyField}

