mapper.select:
select  *  from yb_appeal_result_deductimplement_view
where  1=1
     and serialNo like concat('%', #{ ybAppealResultDeductimplementView.serialNo} ,'%')
     and billNo like concat('%', #{ ybAppealResultDeductimplementView.billNo} ,'%')
     and proposalCode like concat('%', #{ ybAppealResultDeductimplementView.proposalCode} ,'%')
     and projectCode like concat('%', #{ ybAppealResultDeductimplementView.projectCode} ,'%')
     and projectName like concat('%', #{ ybAppealResultDeductimplementView.projectName} ,'%')
     and ruleName like concat('%', #{ ybAppealResultDeductimplementView.ruleName} ,'%')
     and deductReason like concat('%', #{ ybAppealResultDeductimplementView.deductReason} ,'%')
     and repaymentReason like concat('%', #{ ybAppealResultDeductimplementView.repaymentReason} ,'%')
     and doctorName like concat('%', #{ ybAppealResultDeductimplementView.doctorName} ,'%')
     and deptCode like concat('%', #{ ybAppealResultDeductimplementView.deptCode} ,'%')
     and deptName like concat('%', #{ ybAppealResultDeductimplementView.deptName} ,'%')

    AND ybAppealResultDeductimplementView.enterHospitalDate  >=   enterHospitalDateFrom}


    AND ybAppealResultDeductimplementView.enterHospitalDate  <=   enterHospitalDateTo}


    AND ybAppealResultDeductimplementView.outHospitalDate  >=   outHospitalDateFrom}


    AND ybAppealResultDeductimplementView.outHospitalDate  <=   outHospitalDateTo}


    AND ybAppealResultDeductimplementView.costDate  >=   costDateFrom}


    AND ybAppealResultDeductimplementView.costDate  <=   costDateTo}

     and enterHospitalDateStr like concat('%', #{ ybAppealResultDeductimplementView.enterHospitalDateStr} ,'%')
     and outHospitalDateStr like concat('%', #{ ybAppealResultDeductimplementView.outHospitalDateStr} ,'%')
     and costDateStr like concat('%', #{ ybAppealResultDeductimplementView.costDateStr} ,'%')
     and hospitalizedNo like concat('%', #{ ybAppealResultDeductimplementView.hospitalizedNo} ,'%')
     and treatmentMode like concat('%', #{ ybAppealResultDeductimplementView.treatmentMode} ,'%')

    AND ybAppealResultDeductimplementView.settlementDate  >=   settlementDateFrom}


    AND ybAppealResultDeductimplementView.settlementDate  <=   settlementDateTo}

     and settlementDateStr like concat('%', #{ ybAppealResultDeductimplementView.settlementDateStr} ,'%')
     and personalNo like concat('%', #{ ybAppealResultDeductimplementView.personalNo} ,'%')
     and insuredName like concat('%', #{ ybAppealResultDeductimplementView.insuredName} ,'%')
     and cardNumber like concat('%', #{ ybAppealResultDeductimplementView.cardNumber} ,'%')
     and areaName like concat('%', #{ ybAppealResultDeductimplementView.areaName} ,'%')
     and versionNumber like concat('%', #{ ybAppealResultDeductimplementView.versionNumber} ,'%')
     and backAppeal like concat('%', #{ ybAppealResultDeductimplementView.backAppeal} ,'%')
 and typeno =#{ ybAppealResultDeductimplementView.typeno} 
     and insuredType like concat('%', #{ ybAppealResultDeductimplementView.insuredType} ,'%')
 and dataType =#{ ybAppealResultDeductimplementView.dataType} 
     and orderNumber like concat('%', #{ ybAppealResultDeductimplementView.orderNumber} ,'%')

    AND ybAppealResultDeductimplementView.applyDate  >=   applyDateFrom}


    AND ybAppealResultDeductimplementView.applyDate  <=   applyDateTo}

     and applyDateStr like concat('%', #{ ybAppealResultDeductimplementView.applyDateStr} ,'%')
     and operatorName like concat('%', #{ ybAppealResultDeductimplementView.operatorName} ,'%')
 and raResetState =#{ ybAppealResultDeductimplementView.raResetState} 
 and raResultState =#{ ybAppealResultDeductimplementView.raResultState} 
 and raRepayState =#{ ybAppealResultDeductimplementView.raRepayState} 
     and id like concat('%', #{ ybAppealResultDeductimplementView.id} ,'%')
     and manageId like concat('%', #{ ybAppealResultDeductimplementView.manageId} ,'%')
     and applyDataId like concat('%', #{ ybAppealResultDeductimplementView.applyDataId} ,'%')
     and verifyId like concat('%', #{ ybAppealResultDeductimplementView.verifyId} ,'%')
     and ar_doctorCode like concat('%', #{ ybAppealResultDeductimplementView.arDoctorcode} ,'%')
     and ar_doctorName like concat('%', #{ ybAppealResultDeductimplementView.arDoctorname} ,'%')
     and ar_deptCode like concat('%', #{ ybAppealResultDeductimplementView.arDeptcode} ,'%')
     and ar_deptName like concat('%', #{ ybAppealResultDeductimplementView.arDeptname} ,'%')
     and operateReason like concat('%', #{ ybAppealResultDeductimplementView.operateReason} ,'%')

    AND ybAppealResultDeductimplementView.operateDate  >=   operateDateFrom}


    AND ybAppealResultDeductimplementView.operateDate  <=   operateDateTo}

     and COMMENTS like concat('%', #{ ybAppealResultDeductimplementView.comments} ,'%')
 and STATE =#{ ybAppealResultDeductimplementView.state} 
 and IS_DELETEMARK =#{ ybAppealResultDeductimplementView.isDeletemark} 

    AND ybAppealResultDeductimplementView.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybAppealResultDeductimplementView.MODIFY_TIME  <=   modifyTimeTo}


    AND ybAppealResultDeductimplementView.CREATE_TIME  >=   createTimeFrom}


    AND ybAppealResultDeductimplementView.CREATE_TIME  <=   createTimeTo}

 and sourceType =#{ ybAppealResultDeductimplementView.sourceType} 
     and resetDataId like concat('%', #{ ybAppealResultDeductimplementView.resetDataId} ,'%')
 and repayState =#{ ybAppealResultDeductimplementView.repayState} 
     and currencyField like concat('%', #{ ybAppealResultDeductimplementView.currencyField} ,'%')

    AND ybAppealResultDeductimplementView.implementDate  >=   implementDateFrom}


    AND ybAppealResultDeductimplementView.implementDate  <=   implementDateTo}

     and implementDateStr like concat('%', #{ ybAppealResultDeductimplementView.implementDateStr} ,'%')
 and shareState =#{ ybAppealResultDeductimplementView.shareState} 
     and shareProgramme like concat('%', #{ ybAppealResultDeductimplementView.shareProgramme} ,'%')
