mapper.select:
select * from yb_appeal_result
where 1=1
and id =#{ ybAppealResult.id}
and applyDataId
    =#{ ybAppealResult.applyDataId}

and verifyId
    =#{ ybAppealResult.verifyId}

and manageId
    =#{ ybAppealResult.manageId}

and doctorCode like concat('%',
    #{ ybAppealResult.doctorCode} ,'%')

and doctorName like concat('%',
    #{ ybAppealResult.doctorName} ,'%')

and deptCode like concat('%',
    #{ ybAppealResult.deptCode} ,'%')

and deptName like concat('%',
    #{ ybAppealResult.deptName} ,'%')

and operateReason like
    concat('%', #{ ybAppealResult.operateReason} ,'%')


    AND ybAppealResult.operateDate  >=   operateDateFrom}


    AND ybAppealResult.operateDate  <=   operateDateTo}

and currencyField like
    concat('%', #{ ybAppealResult.currencyField} ,'%')

and sourceType =#{ ybAppealResult.sourceType}
and repayState =#{ ybAppealResult.repayState}

    AND ybAppealResult.resetDate  >=   resetDateFrom}


    AND ybAppealResult.resetDate  <=   resetDateTo}

and COMMENTS like concat('%',
    #{ ybAppealResult.comments} ,'%')

and STATE =#{ ybAppealResult.state}

    AND ybAppealResult.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybAppealResult.MODIFY_TIME  <=   modifyTimeTo}


    AND ybAppealResult.CREATE_TIME  >=   createTimeFrom}


    AND ybAppealResult.CREATE_TIME  <=   createTimeTo}

SELECT yb_appeal_result.deptName FROM yb_appeal_result INNER JOIN com_file ON com_file.REF_TAB_ID = yb_appeal_result.id INNER JOIN yb_reconsider_apply_data ON yb_reconsider_apply_data.id = yb_appeal_result.applyDataId WHERE yb_reconsider_apply_data.pid = #{ybAppealResultView.pid} and yb_appeal_result.sourceType = #{ybAppealResultView.sourceType}
and yb_appeal_result.typeno = #{ybAppealResultView.typeno}
and yb_appeal_result.dataType = #{ybAppealResultView.dataType}
and yb_appeal_result.STATE = 1 and yb_appeal_result.STATE = 2 group BY yb_appeal_result.deptName

mapper.update:
update yb_appeal_result set relatelDataId = null, resetDate = null, repayState = 1, state = 1 where id in (
#{item.id}
)
