mapper.select:
select * from yb_appeal_result
where 1=1
and id =#{ ybAppealResult.id}
and applyDataId
    =#{ ybAppealResult.applyDataId}

and verifyId
    =#{ ybAppealResult.verifyId}

and manageId
    =#{ ybAppealResult.manageId}

and doctorCode like concat('%',
    #{ ybAppealResult.doctorCode} ,'%')

and doctorName like concat('%',
    #{ ybAppealResult.doctorName} ,'%')

and deptCode like concat('%',
    #{ ybAppealResult.deptCode} ,'%')

and deptName like concat('%',
    #{ ybAppealResult.deptName} ,'%')

and operateReason like
    concat('%', #{ ybAppealResult.operateReason} ,'%')


    AND ybAppealResult.operateDate  >=   operateDateFrom}


    AND ybAppealResult.operateDate  <=   operateDateTo}

and sourceType =#{ ybAppealResult.sourceType}
and repayState =#{ ybAppealResult.repayState}

    AND ybAppealResult.resetDate  >=   resetDateFrom}


    AND ybAppealResult.resetDate  <=   resetDateTo}

and COMMENTS like concat('%',
    #{ ybAppealResult.comments} ,'%')

and STATE =#{ ybAppealResult.state}
and ybAppealResult.areaType =#{ ybAppealResult.areaType}

SELECT id,asCode deptId,asName deptName FROM yb_appeal_sumdept WHERE IS_DELETEMARK = 1 and
areaType = #{ybAppealResultView.areaType} and
ID IN( select DISTINCT pid from yb_appeal_sumdept_data where deptId in ( SELECT yb_appeal_result.deptCode FROM yb_appeal_result INNER JOIN com_file ON com_file.REF_TAB_ID = yb_appeal_result.id and com_file.IS_DELETEMARK = 1 WHERE yb_appeal_result.applyDateStr = #{ybAppealResultView.applyDateStr}
and yb_appeal_result.areaType = #{ybAppealResultView.areaType}
and yb_appeal_result.sourceType = #{ybAppealResultView.sourceType}
and yb_appeal_result.dataType = #{ybAppealResultView.dataType}
and yb_appeal_result.typeno = #{ybAppealResultView.typeno}
and yb_appeal_result.STATE = 1
and yb_appeal_result.STATE = 2
group by yb_appeal_result.deptCode ) )

SELECT uuid() id,yb_appeal_result.deptCode deptId,yb_appeal_result.deptName FROM yb_appeal_result INNER JOIN com_file ON com_file.REF_TAB_ID = yb_appeal_result.id and com_file.IS_DELETEMARK = 1 WHERE yb_appeal_result.applyDateStr = #{ybAppealResultView.applyDateStr}
and yb_appeal_result.areaType = #{ybAppealResultView.areaType}
and yb_appeal_result.sourceType = #{ybAppealResultView.sourceType}
and yb_appeal_result.dataType = #{ybAppealResultView.dataType}
and yb_appeal_result.typeno = #{ybAppealResultView.typeno}
and yb_appeal_result.STATE = 1
and yb_appeal_result.STATE = 2
group BY yb_appeal_result.deptCode,yb_appeal_result.deptName

SELECT uuid() id, yb_appeal_result.deptCode deptId, yb_appeal_result.deptName FROM yb_appeal_result INNER JOIN com_file ON com_file.REF_TAB_ID = yb_appeal_result.id AND com_file.IS_DELETEMARK = 1 WHERE yb_appeal_result.applyDateStr = #{ybAppealResultView.applyDateStr}
and yb_appeal_result.areaType = #{ybAppealResultView.areaType}
and yb_appeal_result.sourceType = #{ybAppealResultView.sourceType}
and yb_appeal_result.dataType = #{ybAppealResultView.dataType}
and yb_appeal_result.typeno = #{ybAppealResultView.typeno}
and yb_appeal_result.STATE = 1
and yb_appeal_result.STATE = 2
AND yb_appeal_result.deptCode NOT IN( SELECT yb_appeal_sumdept_data.deptId FROM yb_appeal_sumdept_data inner join yb_appeal_sumdept on yb_appeal_sumdept.id = yb_appeal_sumdept_data.pid WHERE IS_DELETEMARK = 1
and areaType = #{ybAppealResultView.areaType}
) GROUP BY yb_appeal_result.deptCode ,yb_appeal_result.deptName

SELECT
*
FROM
yb_appeal_result
where
applyDateStr = #{ applyDateStr}
AND areaType =#{ areaType}
and sourceType = 0
and dataType =#{ dataType}
AND state = 1

mapper.update:
update yb_appeal_result set relatelDataId = null, resetDate = null, repayState = 1, state = 1 where id in (
#{item.id}
)
