mapper.update:
update yb_appeal_result_repayment
resultId=#{resultId}, resetDataId=#{resetDataId}, deductImplementId=#{deductImplementId}, repaymentProgramme=#{repaymentProgramme}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select  *  from yb_appeal_result_repayment
where  1=1
     and id =#{ ybAppealResultRepayment.id} 
     and resultId like concat('%', #{ ybAppealResultRepayment.resultId} ,'%')
     and resetDataId like concat('%', #{ ybAppealResultRepayment.resetDataId} ,'%')
     and deductImplementId like concat('%', #{ ybAppealResultRepayment.deductImplementId} ,'%')
     and repaymentProgramme like concat('%', #{ ybAppealResultRepayment.repaymentProgramme} ,'%')
     and COMMENTS like concat('%', #{ ybAppealResultRepayment.comments} ,'%')
 and STATE =#{ ybAppealResultRepayment.state} 
 and IS_DELETEMARK =#{ ybAppealResultRepayment.isDeletemark} 

    AND ybAppealResultRepayment.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybAppealResultRepayment.MODIFY_TIME  <=   modifyTimeTo}


    AND ybAppealResultRepayment.CREATE_TIME  >=   createTimeFrom}


    AND ybAppealResultRepayment.CREATE_TIME  <=   createTimeTo}
