mapper.select:
SELECT ad.pid, ad.serialNo, ad.billNo, ad.proposalCode, ad.projectCode, ad.projectName, ad.num, ad.medicalPrice, ad.ruleName, ad.deductPrice, ad.deductReason, ad.repaymentReason, ad.doctorName, ad.deptCode, ad.deptName, ad.enterHospitalDate, ad.outHospitalDate, ad.costDate, ad.enterHospitalDateStr, ad.outHospitalDateStr, ad.costDateStr, ad.hospitalizedNo, ad.treatmentMode, ad.settlementDate, ad.settlementDateStr, ad.personalNo, ad.insuredName, ad.cardNumber, ad.areaName, ad.versionNumber, ad.backAppeal, art.typeno, ad.insuredType, art.dataType, art.orderNumber, art.orderNum, art.applyDateStr, art.id, art.manageId, art.applyDataId, art.verifyId, art.doctorCode arDoctorCode, art.doctorName arDoctorName, art.deptCode arDeptCode, art.deptName arDeptName, art.operateReason, art.operateDate, art.STATE, art.sourceType, art.repayState, '' currencyField, 0 repaymentPrice, art.relatelDataId, CASE WHEN art.state = 1 or (art.state = 2 and (art.repayState = 1 or art.repayState = 3)) THEN 1 WHEN art.state = 2 and art.repayState = 2 THEN 0 ELSE 2 END isSuccess, 0 adjustPrice, art.areaType FROM yb_appeal_result art STRAIGHT_JOIN yb_reconsider_apply_data ad on ad.id = art.applyDataId where
art.applyDateStr in
#{item}
1=2
and art.areaType = #{ybAppealResultReportView.areaType}
and art.sourceType = #{ybAppealResultReportView.sourceType}
and art.dataType = #{ybAppealResultReportView.dataType}
and (art.state = 1 or (art.state = 2 and (art.repayState = 1 or art.repayState = 3)))
and art.state = 2 and art.repayState = 2
and art.typeno = #{ybAppealResultReportView.typeno}
and art.dataType = #{ybAppealResultReportView.dataType}
and art.doctorCode = #{ybAppealResultReportView.arDoctorCode}
and (ad.serialNo like concat('%', #{ ybAppealResultReportView.currencyField} ,'%') or ad.billNo like concat('%', #{ ybAppealResultReportView.currencyField} ,'%') or ad.proposalCode like concat('%', #{ ybAppealResultReportView.currencyField} ,'%') or ad.projectCode like concat('%', #{ ybAppealResultReportView.currencyField} ,'%') or ad.projectName like concat('%', #{ ybAppealResultReportView.currencyField} ,'%') or ad.ruleName like concat('%', #{ ybAppealResultReportView.currencyField} ,'%'))
and (ad.serialNo like concat('%', #{ ybAppealResultReportView.currencyField} ,'%') or ad.billNo like concat('%', #{ ybAppealResultReportView.currencyField} ,'%') or ad.proposalCode like concat('%', #{ ybAppealResultReportView.currencyField} ,'%') or ad.ruleName like concat('%', #{ ybAppealResultReportView.currencyField} ,'%'))
and (ad.serialNo like concat('%', #{ ybAppealResultReportView.currencyField} ,'%') or ad.billNo like concat('%', #{ ybAppealResultReportView.currencyField} ,'%') or ad.proposalCode like concat('%', #{ ybAppealResultReportView.currencyField} ,'%') or ad.projectCode like concat('%', #{ ybAppealResultReportView.currencyField} ,'%') or ad.projectName like concat('%', #{ ybAppealResultReportView.currencyField} ,'%') or ad.ruleName like concat('%', #{ ybAppealResultReportView.currencyField} ,'%'))
order by art.applyDateStr,art.typeno,art.dataType,art.orderNum asc

