mapper.select:
select  *  from yb_appeal_result_report_view
where  1=1
     and serialNo like concat('%', #{ ybAppealResultReportView.serialNo} ,'%')
     and billNo like concat('%', #{ ybAppealResultReportView.billNo} ,'%')
     and proposalCode like concat('%', #{ ybAppealResultReportView.proposalCode} ,'%')
     and projectCode like concat('%', #{ ybAppealResultReportView.projectCode} ,'%')
     and projectName like concat('%', #{ ybAppealResultReportView.projectName} ,'%')
     and ruleName like concat('%', #{ ybAppealResultReportView.ruleName} ,'%')
     and deductReason like concat('%', #{ ybAppealResultReportView.deductReason} ,'%')
     and repaymentReason like concat('%', #{ ybAppealResultReportView.repaymentReason} ,'%')
     and doctorName like concat('%', #{ ybAppealResultReportView.doctorName} ,'%')
     and deptCode like concat('%', #{ ybAppealResultReportView.deptCode} ,'%')
     and deptName like concat('%', #{ ybAppealResultReportView.deptName} ,'%')

    AND ybAppealResultReportView.enterHospitalDate  >=   enterHospitalDateFrom}


    AND ybAppealResultReportView.enterHospitalDate  <=   enterHospitalDateTo}


    AND ybAppealResultReportView.outHospitalDate  >=   outHospitalDateFrom}


    AND ybAppealResultReportView.outHospitalDate  <=   outHospitalDateTo}


    AND ybAppealResultReportView.costDate  >=   costDateFrom}


    AND ybAppealResultReportView.costDate  <=   costDateTo}

     and enterHospitalDateStr like concat('%', #{ ybAppealResultReportView.enterHospitalDateStr} ,'%')
     and outHospitalDateStr like concat('%', #{ ybAppealResultReportView.outHospitalDateStr} ,'%')
     and costDateStr like concat('%', #{ ybAppealResultReportView.costDateStr} ,'%')
     and hospitalizedNo like concat('%', #{ ybAppealResultReportView.hospitalizedNo} ,'%')
     and treatmentMode like concat('%', #{ ybAppealResultReportView.treatmentMode} ,'%')

    AND ybAppealResultReportView.settlementDate  >=   settlementDateFrom}


    AND ybAppealResultReportView.settlementDate  <=   settlementDateTo}

     and settlementDateStr like concat('%', #{ ybAppealResultReportView.settlementDateStr} ,'%')
     and personalNo like concat('%', #{ ybAppealResultReportView.personalNo} ,'%')
     and insuredName like concat('%', #{ ybAppealResultReportView.insuredName} ,'%')
     and cardNumber like concat('%', #{ ybAppealResultReportView.cardNumber} ,'%')
     and areaName like concat('%', #{ ybAppealResultReportView.areaName} ,'%')
     and versionNumber like concat('%', #{ ybAppealResultReportView.versionNumber} ,'%')
     and backAppeal like concat('%', #{ ybAppealResultReportView.backAppeal} ,'%')
 and typeno =#{ ybAppealResultReportView.typeno} 
     and insuredType like concat('%', #{ ybAppealResultReportView.insuredType} ,'%')
 and dataType =#{ ybAppealResultReportView.dataType} 
     and orderNumber like concat('%', #{ ybAppealResultReportView.orderNumber} ,'%')

    AND ybAppealResultReportView.applyDate  >=   applyDateFrom}


    AND ybAppealResultReportView.applyDate  <=   applyDateTo}

     and applyDateStr like concat('%', #{ ybAppealResultReportView.applyDateStr} ,'%')
     and operatorName like concat('%', #{ ybAppealResultReportView.operatorName} ,'%')
 and raResetState =#{ ybAppealResultReportView.raResetState} 
 and raResultState =#{ ybAppealResultReportView.raResultState} 
 and raRepayState =#{ ybAppealResultReportView.raRepayState} 
     and id like concat('%', #{ ybAppealResultReportView.id} ,'%')
     and manageId like concat('%', #{ ybAppealResultReportView.manageId} ,'%')
     and applyDataId like concat('%', #{ ybAppealResultReportView.applyDataId} ,'%')
     and verifyId like concat('%', #{ ybAppealResultReportView.verifyId} ,'%')
     and arDoctorCode like concat('%', #{ ybAppealResultReportView.arDoctorCode} ,'%')
     and arDoctorName like concat('%', #{ ybAppealResultReportView.arDoctorName} ,'%')
     and arDeptCode like concat('%', #{ ybAppealResultReportView.arDeptCode} ,'%')
     and arDeptName like concat('%', #{ ybAppealResultReportView.arDeptName} ,'%')
     and operateReason like concat('%', #{ ybAppealResultReportView.operateReason} ,'%')

    AND ybAppealResultReportView.operateDate  >=   operateDateFrom}


    AND ybAppealResultReportView.operateDate  <=   operateDateTo}

     and COMMENTS like concat('%', #{ ybAppealResultReportView.comments} ,'%')
 and STATE =#{ ybAppealResultReportView.state} 
 and IS_DELETEMARK =#{ ybAppealResultReportView.isDeletemark} 

    AND ybAppealResultReportView.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybAppealResultReportView.MODIFY_TIME  <=   modifyTimeTo}


    AND ybAppealResultReportView.CREATE_TIME  >=   createTimeFrom}


    AND ybAppealResultReportView.CREATE_TIME  <=   createTimeTo}

 and sourceType =#{ ybAppealResultReportView.sourceType} 
     and resetDataId like concat('%', #{ ybAppealResultReportView.resetDataId} ,'%')
 and repayState =#{ ybAppealResultReportView.repayState} 
     and currencyField like concat('%', #{ ybAppealResultReportView.currencyField} ,'%')
 and isSuccess =#{ ybAppealResultReportView.isSuccess}
