mapper.select:
select  *  from yb_appeal_result_view
where  1=1
     and serialNo like concat('%', #{ ybAppealResultView.serialNo} ,'%')
     and billNo like concat('%', #{ ybAppealResultView.billNo} ,'%')
     and proposalCode like concat('%', #{ ybAppealResultView.proposalCode} ,'%')
     and projectCode like concat('%', #{ ybAppealResultView.projectCode} ,'%')
     and projectName like concat('%', #{ ybAppealResultView.projectName} ,'%')
     and ruleName like concat('%', #{ ybAppealResultView.ruleName} ,'%')
     and deductReason like concat('%', #{ ybAppealResultView.deductReason} ,'%')
     and repaymentReason like concat('%', #{ ybAppealResultView.repaymentReason} ,'%')
     and doctorName like concat('%', #{ ybAppealResultView.doctorName} ,'%')
     and deptCode like concat('%', #{ ybAppealResultView.deptCode} ,'%')
     and deptName like concat('%', #{ ybAppealResultView.deptName} ,'%')

    AND ybAppealResultView.enterHospitalDate  >=   enterHospitalDateFrom}


    AND ybAppealResultView.enterHospitalDate  <=   enterHospitalDateTo}


    AND ybAppealResultView.outHospitalDate  >=   outHospitalDateFrom}


    AND ybAppealResultView.outHospitalDate  <=   outHospitalDateTo}


    AND ybAppealResultView.costDate  >=   costDateFrom}


    AND ybAppealResultView.costDate  <=   costDateTo}

     and hospitalizedNo like concat('%', #{ ybAppealResultView.hospitalizedNo} ,'%')
     and treatmentMode like concat('%', #{ ybAppealResultView.treatmentMode} ,'%')

    AND ybAppealResultView.settlementDate  >=   settlementDateFrom}


    AND ybAppealResultView.settlementDate  <=   settlementDateTo}

     and personalNo like concat('%', #{ ybAppealResultView.personalNo} ,'%')
     and insuredName like concat('%', #{ ybAppealResultView.insuredName} ,'%')
     and cardNumber like concat('%', #{ ybAppealResultView.cardNumber} ,'%')
     and areaName like concat('%', #{ ybAppealResultView.areaName} ,'%')
     and versionNumber like concat('%', #{ ybAppealResultView.versionNumber} ,'%')
     and backAppeal like concat('%', #{ ybAppealResultView.backAppeal} ,'%')
 and typeno =#{ ybAppealResultView.typeno} 

    AND ybAppealResultView.applyDate  >=   applyDateFrom}


    AND ybAppealResultView.applyDate  <=   applyDateTo}

     and applyDateStr like concat('%', #{ ybAppealResultView.applyDateStr} ,'%')
     and operatorName like concat('%', #{ ybAppealResultView.operatorName} ,'%')
     and id like concat('%', #{ ybAppealResultView.id} ,'%')
     and applyDataId like concat('%', #{ ybAppealResultView.applyDataId} ,'%')
     and verifyId like concat('%', #{ ybAppealResultView.verifyId} ,'%')
     and ar_doctorCode like concat('%', #{ ybAppealResultView.arDoctorCode} ,'%')
     and ar_doctorName like concat('%', #{ ybAppealResultView.arDoctorname} ,'%')
     and ar_deptCode like concat('%', #{ ybAppealResultView.arDeptcode} ,'%')
     and ar_deptName like concat('%', #{ ybAppealResultView.arDeptname} ,'%')
     and operateReason like concat('%', #{ ybAppealResultView.operateReason} ,'%')

    AND ybAppealResultView.operateDate  >=   operateDateFrom}


    AND ybAppealResultView.operateDate  <=   operateDateTo}

     and COMMENTS like concat('%', #{ ybAppealResultView.comments} ,'%')
 and STATE =#{ ybAppealResultView.state} 
 and IS_DELETEMARK =#{ ybAppealResultView.isDeletemark} 

    AND ybAppealResultView.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybAppealResultView.MODIFY_TIME  <=   modifyTimeTo}


    AND ybAppealResultView.CREATE_TIME  >=   createTimeFrom}


    AND ybAppealResultView.CREATE_TIME  <=   createTimeTo}

     and currencyField like concat('%', #{ ybAppealResultView.currencyField} ,'%')
 and sourceType =#{ ybAppealResult.sourceType}
