select ad.pid, ad.serialNo, ad.billNo, ad.proposalCode, ad.projectCode, ad.projectName, ad.num, ad.medicalPrice, ad.ruleName, ad.deductPrice, ad.deductReason, ad.repaymentReason, ad.doctorName, ad.deptCode, ad.deptName, ad.enterHospitalDateStr, ad.outHospitalDateStr, ad.costDateStr, ad.hospitalizedNo, ad.treatmentMode, ad.settlementDateStr, ad.personalNo, ad.insuredName, ad.insuredType, ad.cardNumber, ad.areaName, ad.versionNumber, ad.backAppeal, art.typeno, art.applyDateStr, art.dataType, art.orderNumber, art.orderNum, art.id, art.applyDataId, art.verifyId, art.doctorCode arDoctorCode, art.doctorName arDoctorName, art.deptCode arDeptCode, art.deptName arDeptName, art.orderDoctorCode arOrderDoctorCode, art.orderDoctorName arOrderDoctorName, art.orderDeptCode arOrderDeptCode, art.orderDeptName arOrderDeptName, art.operateReason, art.operateDate, art.sourceType, art.STATE, art.repayState, '' currencyField, art.relatelDataId, art.areaType from yb_appeal_result art STRAIGHT_JOIN yb_reconsider_apply_data ad ON art.applyDataId = ad.id where art.applyDateStr = #{ ybAppealResultView.applyDateStr }
and art.areaType = #{ybAppealResultView.areaType}
and art.sourceType = #{ybAppealResultView.sourceType}
and art.typeno = #{ybAppealResultView.typeno}
and art.dataType = #{ybAppealResultView.dataType}
and art.state = #{ybAppealResultView.state}
and art.relatelDataId = #{ybAppealResultView.relatelDataId}
and ad.billNo = #{ybAppealResultView.billNo}
and ad.serialNo = #{ybAppealResultView.serialNo}
and ad.projectCode = #{ybAppealResultView.projectCode}
and ad.projectName = #{ybAppealResultView.projectName}
and ad.personalNo = #{ybAppealResultView.personalNo}
and ad.ruleName = #{ybAppealResultView.ruleName}
and (ad.serialNo like concat('%', #{ ybAppealResultView.currencyField} ,'%') or ad.proposalCode like concat('%', #{ ybAppealResultView.currencyField} ,'%') or ad.billNo like concat('%', #{ ybAppealResultView.currencyField} ,'%') or ad.projectCode like concat('%', #{ ybAppealResultView.currencyField} ,'%') or ad.projectName like concat('%', #{ ybAppealResultView.currencyField} ,'%') or ad.ruleName like concat('%', #{ ybAppealResultView.currencyField} ,'%'))
and (ad.serialNo like concat('%', #{ ybAppealResultView.currencyField} ,'%') or ad.proposalCode like concat('%', #{ ybAppealResultView.currencyField} ,'%') or ad.billNo like concat('%', #{ ybAppealResultView.currencyField} ,'%') or ad.ruleName like concat('%', #{ ybAppealResultView.currencyField} ,'%'))
and (ad.serialNo like concat('%', #{ ybAppealResultView.currencyField} ,'%') or ad.proposalCode like concat('%', #{ ybAppealResultView.currencyField} ,'%') or ad.billNo like concat('%', #{ ybAppealResultView.currencyField} ,'%') or ad.projectCode like concat('%', #{ ybAppealResultView.currencyField} ,'%') or ad.projectName like concat('%', #{ ybAppealResultView.currencyField} ,'%') or ad.ruleName like concat('%', #{ ybAppealResultView.currencyField} ,'%'))
order by art.orderNum asc

select
    count(1)
from
yb_appeal_result art
STRAIGHT_JOIN yb_reconsider_apply_data ad ON
    art.applyDataId = ad.id
where
    art.applyDateStr = #{ ybAppealResultView.applyDateStr }

    and art.areaType = #{ybAppealResultView.areaType}


    and art.sourceType = #{ybAppealResultView.sourceType}


    and art.typeno = #{ybAppealResultView.typeno}


    and art.dataType = #{ybAppealResultView.dataType}


    and art.state = #{ybAppealResultView.state}


    and art.relatelDataId = #{ybAppealResultView.relatelDataId}


    and ad.billNo = #{ybAppealResultView.billNo}


    and ad.serialNo = #{ybAppealResultView.serialNo}


    and ad.projectCode = #{ybAppealResultView.projectCode}


    and ad.projectName = #{ybAppealResultView.projectName}


    and ad.personalNo = #{ybAppealResultView.personalNo}


    and ad.ruleName = #{ybAppealResultView.ruleName}


    and (ad.serialNo like concat('%', #{ ybAppealResultView.currencyField} ,'%')
    or ad.proposalCode like concat('%', #{ ybAppealResultView.currencyField} ,'%')
    or ad.billNo like concat('%', #{ ybAppealResultView.currencyField} ,'%')
    or ad.projectCode like concat('%', #{ ybAppealResultView.currencyField} ,'%')
    or ad.projectName like concat('%', #{ ybAppealResultView.currencyField} ,'%')
    or ad.ruleName like concat('%', #{ ybAppealResultView.currencyField} ,'%'))


    and (ad.serialNo like concat('%', #{ ybAppealResultView.currencyField} ,'%')
    or ad.proposalCode like concat('%', #{ ybAppealResultView.currencyField} ,'%')
    or ad.billNo like concat('%', #{ ybAppealResultView.currencyField} ,'%')
    or ad.ruleName like concat('%', #{ ybAppealResultView.currencyField} ,'%'))


    and (ad.serialNo like concat('%', #{ ybAppealResultView.currencyField} ,'%')
    or ad.proposalCode like concat('%', #{ ybAppealResultView.currencyField} ,'%')
    or ad.billNo like concat('%', #{ ybAppealResultView.currencyField} ,'%')
    or ad.projectCode like concat('%', #{ ybAppealResultView.currencyField} ,'%')
    or ad.projectName like concat('%', #{ ybAppealResultView.currencyField} ,'%')
    or ad.ruleName like concat('%', #{ ybAppealResultView.currencyField} ,'%'))

