mapper.update:
update yb_appeal_sumdept
asCode=#{asCode}, asName=#{asName}, currencyField=#{currencyField}, asType=#{asType}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select  *  from yb_appeal_sumdept
where  1=1
 and id =#{ ybAppealSumdept.id} 
     and asCode like concat('%', #{ ybAppealSumdept.asCode} ,'%')
     and asName like concat('%', #{ ybAppealSumdept.asName} ,'%')
     and currencyField like concat('%', #{ ybAppealSumdept.currencyField} ,'%')
 and asType =#{ ybAppealSumdept.asType} 
     and COMMENTS like concat('%', #{ ybAppealSumdept.comments} ,'%')
 and STATE =#{ ybAppealSumdept.state} 
 and IS_DELETEMARK =#{ ybAppealSumdept.isDeletemark} 

    AND ybAppealSumdept.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybAppealSumdept.MODIFY_TIME  <=   modifyTimeTo}


    AND ybAppealSumdept.CREATE_TIME  >=   createTimeFrom}


    AND ybAppealSumdept.CREATE_TIME  <=   createTimeTo}
