mapper.update:
update yb_handle_verify_data
pid=#{pid}, resetId=#{resetId}, applyDataId=#{applyDataId}, verifyId=#{verifyId}, manageId=#{manageId}, doctorCode=#{doctorCode}, doctorName=#{doctorName}, deptCode=#{deptCode}, deptName=#{deptName}, operateReason=#{operateReason}, operateDate=#{operateDate}, matchPersonId=#{matchPersonId}, matchPersonName=#{matchPersonName}, matchDate=#{matchDate}, reviewerId=#{reviewerId}, reviewerName=#{reviewerName}, reviewerDate=#{reviewerDate}, sendPersonId=#{sendPersonId}, sendPersonName=#{sendPersonName}, sendDate=#{sendDate}, currencyField=#{currencyField}, dataType=#{dataType}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, resultId=#{resultId},
where id=#{id}

mapper.select:
select  *  from yb_handle_verify_data
where  1=1
     and id =#{ ybHandleVerifyData.id} 
     and pid like concat('%', #{ ybHandleVerifyData.pid} ,'%')
     and resetId like concat('%', #{ ybHandleVerifyData.resetId} ,'%')
     and applyDataId like concat('%', #{ ybHandleVerifyData.applyDataId} ,'%')
     and verifyId like concat('%', #{ ybHandleVerifyData.verifyId} ,'%')
     and manageId like concat('%', #{ ybHandleVerifyData.manageId} ,'%')
     and doctorCode like concat('%', #{ ybHandleVerifyData.doctorCode} ,'%')
     and doctorName like concat('%', #{ ybHandleVerifyData.doctorName} ,'%')
     and deptCode like concat('%', #{ ybHandleVerifyData.deptCode} ,'%')
     and deptName like concat('%', #{ ybHandleVerifyData.deptName} ,'%')
     and operateReason like concat('%', #{ ybHandleVerifyData.operateReason} ,'%')

    AND ybHandleVerifyData.operateDate  >=   operateDateFrom}


    AND ybHandleVerifyData.operateDate  <=   operateDateTo}

     and matchPersonName like concat('%', #{ ybHandleVerifyData.matchPersonName} ,'%')

    AND ybHandleVerifyData.matchDate  >=   matchDateFrom}


    AND ybHandleVerifyData.matchDate  <=   matchDateTo}

     and reviewerName like concat('%', #{ ybHandleVerifyData.reviewerName} ,'%')

    AND ybHandleVerifyData.reviewerDate  >=   reviewerDateFrom}


    AND ybHandleVerifyData.reviewerDate  <=   reviewerDateTo}

     and sendPersonName like concat('%', #{ ybHandleVerifyData.sendPersonName} ,'%')

    AND ybHandleVerifyData.sendDate  >=   sendDateFrom}


    AND ybHandleVerifyData.sendDate  <=   sendDateTo}

     and currencyField like concat('%', #{ ybHandleVerifyData.currencyField} ,'%')
 and dataType =#{ ybHandleVerifyData.dataType} 
     and COMMENTS like concat('%', #{ ybHandleVerifyData.comments} ,'%')
 and STATE =#{ ybHandleVerifyData.state} 
 and IS_DELETEMARK =#{ ybHandleVerifyData.isDeletemark} 

    AND ybHandleVerifyData.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybHandleVerifyData.MODIFY_TIME  <=   modifyTimeTo}


    AND ybHandleVerifyData.CREATE_TIME  >=   createTimeFrom}


    AND ybHandleVerifyData.CREATE_TIME  <=   createTimeTo}

     and resultId like concat('%', #{ ybHandleVerifyData.resultId} ,'%')
