select rrd.serialNo, rrd.billNo, rrd.projectCode, rrd.projectName, rrd.medicalPrice, rrd.ruleName, rrd.deductPrice, rrd.deductReason, rrd.repaymentReason, rrd.doctorName, rrd.deptCode, rrd.deptName, rrd.costDate, rrd.costDateStr, rrd.hospitalizedNo, rrd.treatmentMode, rrd.settlementDate, rrd.settlementDateStr, rrd.personalNo, rrd.insuredName, rrd.cardNumber, rrd.areaName, rrd.insuredType, rrd.dataType, rrd.orderNumber, rrd.orderNum, #{ybHandleVerifyDataView.applyDateStr} applyDateStr, hvd.id, hvd.pid, hvd.applyDataId, hvd.verifyId, hvd.manageId, hvd.resultId, hvd.doctorCode hvDoctorCode, hvd.doctorName hvDoctorName, hvd.deptCode hvDeptCode, hvd.deptName hvDeptName, hvd.STATE, hvd.relatelDataId, '' currencyField, hvd.dksName from yb_handle_verify_data hvd INNER JOIN yb_reconsider_reset_data rrd ON rrd.relatelDataId = hvd.relatelDataId WHERE
hvd.pid= #{ybHandleVerifyDataView.pid}
1=2
and hvd.state= #{ybHandleVerifyDataView.state}
and hvd.dataType= #{ybHandleVerifyDataView.dataType}
and (rrd.orderNumber like concat('%', #{ ybHandleVerifyDataView.currencyField} ,'%') or rrd.serialNo like concat('%', #{ ybHandleVerifyDataView.currencyField} ,'%') or rrd.billNo like concat('%', #{ ybHandleVerifyDataView.currencyField} ,'%') or rrd.projectCode like concat('%', #{ ybHandleVerifyDataView.currencyField} ,'%') or rrd.projectName like concat('%', #{ ybHandleVerifyDataView.currencyField} ,'%') or rrd.ruleName like concat('%', #{ ybHandleVerifyDataView.currencyField} ,'%'))
and (rrd.orderNumber like concat('%', #{ ybHandleVerifyDataView.currencyField} ,'%') or rrd.serialNo like concat('%', #{ ybHandleVerifyDataView.currencyField} ,'%') or rrd.billNo like concat('%', #{ ybHandleVerifyDataView.currencyField} ,'%') or rrd.ruleName like concat('%', #{ ybHandleVerifyDataView.currencyField} ,'%'))
and (rrd.orderNumber like concat('%', #{ ybHandleVerifyDataView.currencyField} ,'%') or rrd.serialNo like concat('%', #{ ybHandleVerifyDataView.currencyField} ,'%') or rrd.billNo like concat('%', #{ ybHandleVerifyDataView.currencyField} ,'%') or rrd.projectCode like concat('%', #{ ybHandleVerifyDataView.currencyField} ,'%') or rrd.projectName like concat('%', #{ ybHandleVerifyDataView.currencyField} ,'%') or rrd.ruleName like concat('%', #{ ybHandleVerifyDataView.currencyField} ,'%'))

