AND ybHandleVerifyDataView.enterHospitalDate >= enterHospitalDateFrom}
AND ybHandleVerifyDataView.enterHospitalDate <= enterHospitalDateTo}
AND ybHandleVerifyDataView.outHospitalDate >= outHospitalDateFrom}
AND ybHandleVerifyDataView.outHospitalDate <= outHospitalDateTo}
AND ybHandleVerifyDataView.costDate >= costDateFrom}
AND ybHandleVerifyDataView.costDate <= costDateTo}
and enterHospitalDateStr like concat('%', #{ ybHandleVerifyDataView.enterHospitalDateStr} ,'%') and outHospitalDateStr like concat('%', #{ ybHandleVerifyDataView.outHospitalDateStr} ,'%') and costDateStr like concat('%', #{ ybHandleVerifyDataView.costDateStr} ,'%') and hospitalizedNo like concat('%', #{ ybHandleVerifyDataView.hospitalizedNo} ,'%') and treatmentMode like concat('%', #{ ybHandleVerifyDataView.treatmentMode} ,'%')
AND ybHandleVerifyDataView.settlementDate >= settlementDateFrom}
AND ybHandleVerifyDataView.settlementDate <= settlementDateTo}
and settlementDateStr like concat('%', #{ ybHandleVerifyDataView.settlementDateStr} ,'%') and personalNo like concat('%', #{ ybHandleVerifyDataView.personalNo} ,'%') and insuredName like concat('%', #{ ybHandleVerifyDataView.insuredName} ,'%') and cardNumber like concat('%', #{ ybHandleVerifyDataView.cardNumber} ,'%') and areaName like concat('%', #{ ybHandleVerifyDataView.areaName} ,'%') and versionNumber like concat('%', #{ ybHandleVerifyDataView.versionNumber} ,'%') and backAppeal like concat('%', #{ ybHandleVerifyDataView.backAppeal} ,'%') and typeno =#{ ybHandleVerifyDataView.typeno} and insuredType like concat('%', #{ ybHandleVerifyDataView.insuredType} ,'%') and dataType =#{ ybHandleVerifyDataView.dataType} and orderNumber like concat('%', #{ ybHandleVerifyDataView.orderNumber} ,'%')
AND ybHandleVerifyDataView.applyDate >= applyDateFrom}
AND ybHandleVerifyDataView.applyDate <= applyDateTo}
and applyDateStr like concat('%', #{ ybHandleVerifyDataView.applyDateStr} ,'%') and id like concat('%', #{ ybHandleVerifyDataView.id} ,'%') and pid like concat('%', #{ ybHandleVerifyDataView.pid} ,'%') and applyDataId like concat('%', #{ ybHandleVerifyDataView.applyDataId} ,'%') and verifyId like concat('%', #{ ybHandleVerifyDataView.verifyId} ,'%') and manageId like concat('%', #{ ybHandleVerifyDataView.manageId} ,'%') and resultId like concat('%', #{ ybHandleVerifyDataView.resultId} ,'%') and hvDoctorCode like concat('%', #{ ybHandleVerifyDataView.hvDoctorCode} ,'%') and hvDoctorName like concat('%', #{ ybHandleVerifyDataView.hvDoctorName} ,'%') and hvDeptCode like concat('%', #{ ybHandleVerifyDataView.hvDeptCode} ,'%') and hvDeptName like concat('%', #{ ybHandleVerifyDataView.hvDeptName} ,'%') and operateReason like concat('%', #{ ybHandleVerifyDataView.operateReason} ,'%')
AND ybHandleVerifyDataView.operateDate >= operateDateFrom}
AND ybHandleVerifyDataView.operateDate <= operateDateTo}
and STATE =#{ ybHandleVerifyDataView.state} and IS_DELETEMARK =#{ ybHandleVerifyDataView.isDeletemark} and currencyField like concat('%', #{ ybHandleVerifyDataView.currencyField} ,'%')
