mapper.update:
update yb_notice
ntTitle=#{ntTitle}, ntExplain=#{ntExplain}, ntDetail=#{ntDetail}, sendType=#{sendType}, releaseDate=#{releaseDate}, operatorId=#{operatorId}, operatorName=#{operatorName}, clickNum=#{clickNum}, smsState=#{smsState}, currencyField=#{currencyField}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, areaType=#{areaType},
where id=#{id}

mapper.select:
select  *  from yb_notice
where  1=1
 and id =#{ ybNotice.id} 
     and ntTitle like concat('%', #{ ybNotice.ntTitle} ,'%')
     and ntExplain like concat('%', #{ ybNotice.ntExplain} ,'%')
     and ntDetail like concat('%', #{ ybNotice.ntDetail} ,'%')
 and sendType =#{ ybNotice.sendType} 

    AND ybNotice.releaseDate  >=   releaseDateFrom}


    AND ybNotice.releaseDate  <=   releaseDateTo}

     and operatorName like concat('%', #{ ybNotice.operatorName} ,'%')
 and clickNum =#{ ybNotice.clickNum} 
 and smsState =#{ ybNotice.smsState} 
     and currencyField like concat('%', #{ ybNotice.currencyField} ,'%')
     and COMMENTS like concat('%', #{ ybNotice.comments} ,'%')
 and STATE =#{ ybNotice.state} 
 and IS_DELETEMARK =#{ ybNotice.isDeletemark} 

    AND ybNotice.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybNotice.MODIFY_TIME  <=   modifyTimeTo}


    AND ybNotice.CREATE_TIME  >=   createTimeFrom}


    AND ybNotice.CREATE_TIME  <=   createTimeTo}

and ybNotice.areaType =#{ ybNotice.areaType}
