mapper.update:
update yb_person
personCode=#{personCode}, personName=#{personName}, deptCode=#{deptCode}, deptName=#{deptName}, sex=#{sex}, email=#{email}, tel=#{tel}, spellCode=#{spellCode}, strokeCode=#{strokeCode}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
        select * from yb_person where personCode in(
		SELECT
			readyDoctorCode
		FROM
			yb_appeal_manage
		WHERE
			applyDateStr = #{applyDateStr}
			AND areaType = #{areaType}
			AND acceptState = #{acceptState}
			AND typeno = #{typeno}
			AND sourceType = #{sourceType}
			group by readyDoctorCode
	)

        select * from yb_person where personCode in(
		SELECT
			readyDoctorCode
		FROM
			yb_drg_manage
		WHERE
			applyDateStr = #{applyDateStr}
			AND areaType = #{areaType}
			AND state = #{state}
			group by readyDoctorCode
	)
