mapper.update:
update yb_reconsider_apply_data
pid=#{pid}, serialNo=#{serialNo}, billNo=#{billNo}, proposalCode=#{proposalCode}, projectCode=#{projectCode}, projectName=#{projectName}, num=#{num}, medicalPrice=#{medicalPrice}, ruleName=#{ruleName}, deductPrice=#{deductPrice}, deductReason=#{deductReason}, repaymentReason=#{repaymentReason}, doctorName=#{doctorName}, deptCode=#{deptCode}, deptName=#{deptName}, enterHospitalDate=#{enterHospitalDate}, outHospitalDate=#{outHospitalDate}, costDate=#{costDate}, hospitalizedNo=#{hospitalizedNo}, treatmentMode=#{treatmentMode}, settlementDate=#{settlementDate}, personalNo=#{personalNo}, insuredName=#{insuredName}, cardNumber=#{cardNumber}, areaName=#{areaName}, versionNumber=#{versionNumber}, backAppeal=#{backAppeal}, typeno=#{typeno}, dataType=#{dataType}, orderNumber=#{orderNumber}, orderNum=#{orderNum}, orderSettlementNum=#{orderSettlementNum}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark},
where id=#{id}

mapper.select:
select  *  from yb_reconsider_apply_data
where  1=1
 and id =#{ ybReconsiderApplyData.id} 
 and pid =#{ ybReconsiderApplyData.pid} 
 and dataType =#{ ybReconsiderApplyData.dataType} 
     and serialNo like concat('%', #{ ybReconsiderApplyData.serialNo} ,'%')
     and billNo like concat('%', #{ ybReconsiderApplyData.billNo} ,'%')
     and proposalCode like concat('%', #{ ybReconsiderApplyData.proposalCode} ,'%')
     and projectCode like concat('%', #{ ybReconsiderApplyData.projectCode} ,'%')
     and projectName like concat('%', #{ ybReconsiderApplyData.projectName} ,'%')
     and ruleName like concat('%', #{ ybReconsiderApplyData.ruleName} ,'%')
     and deductReason like concat('%', #{ ybReconsiderApplyData.deductReason} ,'%')
     and repaymentReason like concat('%', #{ ybReconsiderApplyData.repaymentReason} ,'%')
     and doctorName like concat('%', #{ ybReconsiderApplyData.doctorName} ,'%')
     and deptCode like concat('%', #{ ybReconsiderApplyData.deptCode} ,'%')
     and deptName like concat('%', #{ ybReconsiderApplyData.deptName} ,'%')

    and enterHospitalDate  >=   #{ybReconsiderApplyData.enterHospitalDateFrom}


    and enterHospitalDate  <=   #{ybReconsiderApplyData.enterHospitalDateTo}


    and outHospitalDate  >=   #{ybReconsiderApplyData.outHospitalDateFrom}


    and outHospitalDate  <=   #{ybReconsiderApplyData.outHospitalDateTo}


    and costDate  >=   #{ybReconsiderApplyData.costDateFrom}


    and costDate  <=   #{ybReconsiderApplyData.costDateTo}

     and hospitalizedNo like concat('%', #{ ybReconsiderApplyData.hospitalizedNo} ,'%')
     and treatmentMode like concat('%', #{ ybReconsiderApplyData.treatmentMode} ,'%')

    and settlementDate  >=   #{ybReconsiderApplyData.settlementDateFrom}


    and settlementDate  <=   #{ybReconsiderApplyData.settlementDateTo}

     and personalNo like concat('%', #{ ybReconsiderApplyData.personalNo} ,'%')
     and insuredName like concat('%', #{ ybReconsiderApplyData.insuredName} ,'%')
     and cardNumber like concat('%', #{ ybReconsiderApplyData.cardNumber} ,'%')
     and areaName like concat('%', #{ ybReconsiderApplyData.areaName} ,'%')
     and versionNumber like concat('%', #{ ybReconsiderApplyData.versionNumber} ,'%')
     and backAppeal like concat('%', #{ ybReconsiderApplyData.backAppeal} ,'%')
 and typeno =#{ ybReconsiderApplyData.typeno} 
     and COMMENTS like concat('%', #{ ybReconsiderApplyData.comments} ,'%')
 and STATE =#{ ybReconsiderApplyData.state} 
 and IS_DELETEMARK =#{ ybReconsiderApplyData.isDeletemark}

select
  *
from
    yb_reconsider_apply_data
    WHERE
    pid= #{pid} and typeno = #{typeno} and dataType = #{dataType}
    AND id not in(
        select applyDataId from yb_reconsider_verify where
        applyDateStr = #{applyDateStr} and typeno = #{typeno} and dataType = #{dataType} and areaType = #{areaType}
    )

SELECT
    count(1)
FROM
    yb_reconsider_apply_data
where
    pid = #{pid}
     and typeno =#{ typeno} 
     and dataType =#{ dataType}
