mapper.update:
update yb_reconsider_apply
applyDate=#{applyDate}, operatorId=#{operatorId}, operatorName=#{operatorName}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, applyDateStr=#{applyDateStr},
where id=#{id}

mapper.select:
select  *  from yb_reconsider_apply
where  1=1
 and id =#{ ybReconsiderApply.id} 

    AND ybReconsiderApply.applyDate  >=   applyDateFrom}


    AND ybReconsiderApply.applyDate  <=   applyDateTo}

     and operatorName like concat('%', #{ ybReconsiderApply.operatorName} ,'%')
     and COMMENTS like concat('%', #{ ybReconsiderApply.comments} ,'%')
 and STATE =#{ ybReconsiderApply.state} 
 and IS_DELETEMARK =#{ ybReconsiderApply.isDeletemark} 

    AND ybReconsiderApply.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybReconsiderApply.MODIFY_TIME  <=   modifyTimeTo}


    AND ybReconsiderApply.CREATE_TIME  >=   createTimeFrom}


    AND ybReconsiderApply.CREATE_TIME  <=   createTimeTo}

     and applyDateStr like concat('%', #{ ybReconsiderApply.applyDateStr} ,'%')

mapper.delete:
delete from yb_reconsider_apply where id in

    #{item}

and state = #{state}
