mapper.update:
update yb_reconsider_inpatientfees
inpatientId=#{inpatientId}, patientName=#{patientName}, settlementId=#{settlementId}, billNo=#{billNo}, transNo=#{transNo}, itemId=#{itemId}, itemCode=#{itemCode}, itemName=#{itemName}, itemCount=#{itemCount}, itemPrice=#{itemPrice}, itemAmount=#{itemAmount}, feeDate=#{feeDate}, deptId=#{deptId}, deptName=#{deptName}, orderDocId=#{orderDocId}, orderDocName=#{orderDocName}, excuteDeptId=#{excuteDeptId}, excuteDeptName=#{excuteDeptName}, excuteDocId=#{excuteDocId}, excuteDocName=#{excuteDocName}, settlementDate=#{settlementDate}, applyDateStr=#{applyDateStr}, dataType=#{dataType}, typeno=#{typeno}, orderNumber=#{orderNumber}, applyDataId=#{applyDataId}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select  *  from yb_reconsider_inpatientfees
where  1=1
 and id =#{ ybReconsiderInpatientfees.id} 
     and inpatientId like concat('%', #{ ybReconsiderInpatientfees.inpatientId} ,'%')
     and patientName like concat('%', #{ ybReconsiderInpatientfees.patientName} ,'%')
     and settlementId like concat('%', #{ ybReconsiderInpatientfees.settlementId} ,'%')
     and billNo like concat('%', #{ ybReconsiderInpatientfees.billNo} ,'%')
     and transNo like concat('%', #{ ybReconsiderInpatientfees.transNo} ,'%')
     and itemId like concat('%', #{ ybReconsiderInpatientfees.itemId} ,'%')
     and itemCode like concat('%', #{ ybReconsiderInpatientfees.itemCode} ,'%')
     and itemName like concat('%', #{ ybReconsiderInpatientfees.itemName} ,'%')

    AND ybReconsiderInpatientfees.feeDate  >=   feeDateFrom}


    AND ybReconsiderInpatientfees.feeDate  <=   feeDateTo}

     and deptId like concat('%', #{ ybReconsiderInpatientfees.deptId} ,'%')
     and deptName like concat('%', #{ ybReconsiderInpatientfees.deptName} ,'%')
     and orderDocId like concat('%', #{ ybReconsiderInpatientfees.orderDocId} ,'%')
     and orderDocName like concat('%', #{ ybReconsiderInpatientfees.orderDocName} ,'%')
     and excuteDeptId like concat('%', #{ ybReconsiderInpatientfees.excuteDeptId} ,'%')
     and excuteDeptName like concat('%', #{ ybReconsiderInpatientfees.excuteDeptName} ,'%')
     and excuteDocId like concat('%', #{ ybReconsiderInpatientfees.excuteDocId} ,'%')
     and excuteDocName like concat('%', #{ ybReconsiderInpatientfees.excuteDocName} ,'%')
     and applyDateStr = #{ ybReconsiderInpatientfees.applyDateStr} 
 and dataType =#{ ybReconsiderInpatientfees.dataType} 
 and typeno =#{ ybReconsiderInpatientfees.typeno} 
 and applyDataId =#{ ybReconsiderInpatientfees.applyDataId} 

    AND ybReconsiderInpatientfees.settlementDate  >=   settlementDateFrom}


    AND ybReconsiderInpatientfees.settlementDate  <=   settlementDateTo}

     and COMMENTS like concat('%', #{ ybReconsiderInpatientfees.comments} ,'%')
 and STATE =#{ ybReconsiderInpatientfees.state} 
 and IS_DELETEMARK =#{ ybReconsiderInpatientfees.isDeletemark} 

    AND ybReconsiderInpatientfees.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybReconsiderInpatientfees.MODIFY_TIME  <=   modifyTimeTo}


    AND ybReconsiderInpatientfees.CREATE_TIME  >=   createTimeFrom}


    AND ybReconsiderInpatientfees.CREATE_TIME  <=   createTimeTo}

