mapper.update:
update yb_reconsider_priority_level
rplName=#{rplName}, doctorCode=#{doctorCode}, doctorName=#{doctorName}, deptCode=#{deptCode}, deptName=#{deptName}, deptType=#{deptType}, personType=#{personType}, operatorId=#{operatorId}, operatorName=#{operatorName}, currencyField=#{currencyField}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select  *  from yb_reconsider_priority_level
where  1=1
     and id =#{ ybReconsiderPriorityLevel.id} 
     and rplName like concat('%', #{ ybReconsiderPriorityLevel.rplName} ,'%')
     and doctorCode like concat('%', #{ ybReconsiderPriorityLevel.doctorCode} ,'%')
     and doctorName like concat('%', #{ ybReconsiderPriorityLevel.doctorName} ,'%')
     and deptCode like concat('%', #{ ybReconsiderPriorityLevel.deptCode} ,'%')
     and deptName like concat('%', #{ ybReconsiderPriorityLevel.deptName} ,'%')
 and deptType =#{ ybReconsiderPriorityLevel.deptType} 
 and personType =#{ ybReconsiderPriorityLevel.personType} 
     and operatorName like concat('%', #{ ybReconsiderPriorityLevel.operatorName} ,'%')
     and currencyField like concat('%', #{ ybReconsiderPriorityLevel.currencyField} ,'%')
     and COMMENTS like concat('%', #{ ybReconsiderPriorityLevel.comments} ,'%')
 and STATE =#{ ybReconsiderPriorityLevel.state} 
 and IS_DELETEMARK =#{ ybReconsiderPriorityLevel.isDeletemark} 

    AND ybReconsiderPriorityLevel.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybReconsiderPriorityLevel.MODIFY_TIME  <=   modifyTimeTo}


    AND ybReconsiderPriorityLevel.CREATE_TIME  >=   createTimeFrom}


    AND ybReconsiderPriorityLevel.CREATE_TIME  <=   createTimeTo}
