mapper.update:
update yb_reconsider_repay_data
pid=#{pid}, belongDate=#{belongDate}, belongDateStr=#{belongDateStr}, hospitalCode=#{hospitalCode}, hospitalName=#{hospitalName}, orderNumber=#{orderNumber}, serialNo=#{serialNo}, billNo=#{billNo}, projectCode=#{projectCode}, projectName=#{projectName}, medicalPrice=#{medicalPrice}, ruleName=#{ruleName}, deductPrice=#{deductPrice}, deductReason=#{deductReason}, repaymentPrice=#{repaymentPrice}, repaymentReason=#{repaymentReason}, doctorName=#{doctorName}, deptCode=#{deptCode}, deptName=#{deptName}, costDate=#{costDate}, costDateStr=#{costDateStr}, hospitalizedNo=#{hospitalizedNo}, treatmentMode=#{treatmentMode}, settlementDate=#{settlementDate}, settlementDateStr=#{settlementDateStr}, personalNo=#{personalNo}, insuredName=#{insuredName}, resetDataId=#{resetDataId}, resultId=#{resultId}, applyDataId=#{applyDataId}, dataType=#{dataType}, repayType=#{repayType}, seekState=#{seekState}, updateType=#{updateType}, belongDateUpload=#{belongDateUpload}, orderNumberNew=#{orderNumberNew}, orderNum=#{orderNum}, warnType=#{warnType}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select  *  from yb_reconsider_repay_data
where  1=1
     and id =#{ ybReconsiderRepayData.id} 
     and pid like concat('%', #{ ybReconsiderRepayData.pid} ,'%')

    AND ybReconsiderRepayData.belongDate  >=   belongDateFrom}


    AND ybReconsiderRepayData.belongDate  <=   belongDateTo}

     and belongDateStr like concat('%', #{ ybReconsiderRepayData.belongDateStr} ,'%')
     and hospitalCode like concat('%', #{ ybReconsiderRepayData.hospitalCode} ,'%')
     and hospitalName like concat('%', #{ ybReconsiderRepayData.hospitalName} ,'%')
     and orderNumber like concat('%', #{ ybReconsiderRepayData.orderNumber} ,'%')
     and serialNo like concat('%', #{ ybReconsiderRepayData.serialNo} ,'%')
     and billNo like concat('%', #{ ybReconsiderRepayData.billNo} ,'%')
     and projectCode like concat('%', #{ ybReconsiderRepayData.projectCode} ,'%')
     and projectName like concat('%', #{ ybReconsiderRepayData.projectName} ,'%')
     and ruleName like concat('%', #{ ybReconsiderRepayData.ruleName} ,'%')
     and deductReason like concat('%', #{ ybReconsiderRepayData.deductReason} ,'%')
     and repaymentReason like concat('%', #{ ybReconsiderRepayData.repaymentReason} ,'%')
     and doctorName like concat('%', #{ ybReconsiderRepayData.doctorName} ,'%')
     and deptCode like concat('%', #{ ybReconsiderRepayData.deptCode} ,'%')
     and deptName like concat('%', #{ ybReconsiderRepayData.deptName} ,'%')

    AND ybReconsiderRepayData.costDate  >=   costDateFrom}


    AND ybReconsiderRepayData.costDate  <=   costDateTo}

     and costDateStr like concat('%', #{ ybReconsiderRepayData.costDateStr} ,'%')
     and hospitalizedNo like concat('%', #{ ybReconsiderRepayData.hospitalizedNo} ,'%')
     and treatmentMode like concat('%', #{ ybReconsiderRepayData.treatmentMode} ,'%')

    AND ybReconsiderRepayData.settlementDate  >=   settlementDateFrom}


    AND ybReconsiderRepayData.settlementDate  <=   settlementDateTo}

     and settlementDateStr like concat('%', #{ ybReconsiderRepayData.settlementDateStr} ,'%')
     and personalNo like concat('%', #{ ybReconsiderRepayData.personalNo} ,'%')
     and insuredName like concat('%', #{ ybReconsiderRepayData.insuredName} ,'%')
 and resetDataId = #{ ybReconsiderRepayData.resetDataId} 
 and resultId = #{ ybReconsiderRepayData.resultId} 
 and applyDataId = #{ ybReconsiderRepayData.applyDataId} 
 and dataType =#{ ybReconsiderRepayData.dataType} 
 and repayType =#{ ybReconsiderRepayData.repayType} 
 and seekState =#{ ybReconsiderRepayData.seekState} 
 and updateType =#{ ybReconsiderRepayData.updateType} 
 and orderNumberNew like concat('%', #{ ybReconsiderRepayData.orderNumberNew} ,'%')
 and belongDateUpload like concat('%', #{ ybReconsiderRepayData.belongDateUpload} ,'%')
 and warnType =#{ ybReconsiderRepayData.warnType} 
     and COMMENTS like concat('%', #{ ybReconsiderRepayData.comments} ,'%')
 and STATE =#{ ybReconsiderRepayData.state} 
 and IS_DELETEMARK =#{ ybReconsiderRepayData.isDeletemark} 

    AND ybReconsiderRepayData.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybReconsiderRepayData.MODIFY_TIME  <=   modifyTimeTo}


    AND ybReconsiderRepayData.CREATE_TIME  >=   createTimeFrom}


    AND ybReconsiderRepayData.CREATE_TIME  <=   createTimeTo}
