mapper.update:
update yb_reconsider_repay
operatorId=#{operatorId}, operatorName=#{operatorName}, uploadFileName=#{uploadFileName}, repayType=#{repayType}, dataType=#{dataType}, currencyField=#{currencyField}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select  *  from yb_reconsider_repay
where  1=1
     and id =#{ ybReconsiderRepay.id} 
     and operatorName like concat('%', #{ ybReconsiderRepay.operatorName} ,'%')
     and uploadFileName like concat('%', #{ ybReconsiderRepay.uploadFileName} ,'%')
 and repayType =#{ ybReconsiderRepay.repayType} 
 and dataType =#{ ybReconsiderRepay.dataType} 
     and currencyField like concat('%', #{ ybReconsiderRepay.currencyField} ,'%')
     and COMMENTS like concat('%', #{ ybReconsiderRepay.comments} ,'%')
 and STATE =#{ ybReconsiderRepay.state} 
 and IS_DELETEMARK =#{ ybReconsiderRepay.isDeletemark} 

    AND ybReconsiderRepay.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybReconsiderRepay.MODIFY_TIME  <=   modifyTimeTo}


    AND ybReconsiderRepay.CREATE_TIME  >=   createTimeFrom}


    AND ybReconsiderRepay.CREATE_TIME  <=   createTimeTo}
