mapper.update:
update yb_reconsider_reset_data
pid=#{pid}, serialNo=#{serialNo}, billNo=#{billNo}, projectCode=#{projectCode}, projectName=#{projectName}, medicalPrice=#{medicalPrice}, ruleName=#{ruleName}, deductPrice=#{deductPrice}, deductReason=#{deductReason}, repaymentReason=#{repaymentReason}, doctorName=#{doctorName}, deptCode=#{deptCode}, deptName=#{deptName}, costDate=#{costDate}, costDateStr=#{costDateStr}, hospitalizedNo=#{hospitalizedNo}, treatmentMode=#{treatmentMode}, settlementDate=#{settlementDate}, settlementDateStr=#{settlementDateStr}, personalNo=#{personalNo}, insuredName=#{insuredName}, insuredType=#{insuredType}, cardNumber=#{cardNumber}, areaName=#{areaName}, dataType=#{dataType}, orderNumber=#{orderNumber}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select  *  from yb_reconsider_reset_data
where  1=1
     and id =#{ ybReconsiderResetData.id} 
     and pid like concat('%', #{ ybReconsiderResetData.pid} ,'%')
     and serialNo like concat('%', #{ ybReconsiderResetData.serialNo} ,'%')
     and billNo like concat('%', #{ ybReconsiderResetData.billNo} ,'%')
     and projectCode like concat('%', #{ ybReconsiderResetData.projectCode} ,'%')
     and projectName like concat('%', #{ ybReconsiderResetData.projectName} ,'%')
     and ruleName like concat('%', #{ ybReconsiderResetData.ruleName} ,'%')
     and deductReason like concat('%', #{ ybReconsiderResetData.deductReason} ,'%')
     and repaymentReason like concat('%', #{ ybReconsiderResetData.repaymentReason} ,'%')
     and doctorName like concat('%', #{ ybReconsiderResetData.doctorName} ,'%')
     and deptCode like concat('%', #{ ybReconsiderResetData.deptCode} ,'%')
     and deptName like concat('%', #{ ybReconsiderResetData.deptName} ,'%')

    AND ybReconsiderResetData.costDate  >=   costDateFrom}


    AND ybReconsiderResetData.costDate  <=   costDateTo}

     and costDateStr like concat('%', #{ ybReconsiderResetData.costDateStr} ,'%')
     and hospitalizedNo like concat('%', #{ ybReconsiderResetData.hospitalizedNo} ,'%')
     and treatmentMode like concat('%', #{ ybReconsiderResetData.treatmentMode} ,'%')

    AND ybReconsiderResetData.settlementDate  >=   settlementDateFrom}


    AND ybReconsiderResetData.settlementDate  <=   settlementDateTo}

     and settlementDateStr like concat('%', #{ ybReconsiderResetData.settlementDateStr} ,'%')
     and personalNo like concat('%', #{ ybReconsiderResetData.personalNo} ,'%')
     and insuredName like concat('%', #{ ybReconsiderResetData.insuredName} ,'%')
     and cardNumber like concat('%', #{ ybReconsiderResetData.cardNumber} ,'%')
     and areaName like concat('%', #{ ybReconsiderResetData.areaName} ,'%')
     and COMMENTS like concat('%', #{ ybReconsiderResetData.comments} ,'%')
 and STATE =#{ ybReconsiderResetData.state} 
 and IS_DELETEMARK =#{ ybReconsiderResetData.isDeletemark} 

    AND ybReconsiderResetData.MODIFY_TIME  >=   modifyTimeFrom}


    AND ybReconsiderResetData.MODIFY_TIME  <=   modifyTimeTo}


    AND ybReconsiderResetData.CREATE_TIME  >=   createTimeFrom}


    AND ybReconsiderResetData.CREATE_TIME  <=   createTimeTo}
