AND ybReconsiderResetDataView.costDate >= costDateFrom}
AND ybReconsiderResetDataView.costDate <= costDateTo}
and costDateStr like concat('%', #{ ybReconsiderResetDataView.costDateStr} ,'%') and hospitalizedNo like concat('%', #{ ybReconsiderResetDataView.hospitalizedNo} ,'%') and treatmentMode like concat('%', #{ ybReconsiderResetDataView.treatmentMode} ,'%')
