AND ybReconsiderResetDataView.settlementDate <= settlementDateTo}
and settlementDateStr like concat('%', #{ ybReconsiderResetDataView.settlementDateStr} ,'%') and personalNo like concat('%', #{ ybReconsiderResetDataView.personalNo} ,'%') and insuredName like concat('%', #{ ybReconsiderResetDataView.insuredName} ,'%') and cardNumber like concat('%', #{ ybReconsiderResetDataView.cardNumber} ,'%') and areaName like concat('%', #{ ybReconsiderResetDataView.areaName} ,'%') and resetId like concat('%', #{ ybReconsiderResetDataView.resetId} ,'%')
AND ybReconsiderResetDataView.applyDate >= applyDateFrom}
AND ybReconsiderResetDataView.applyDate <= applyDateTo}
and applyDateStr like concat('%', #{ ybReconsiderResetDataView.applyDateStr} ,'%') and applyDataId like concat('%', #{ ybReconsiderResetDataView.applyDataId} ,'%') and reconsiderApplyId like concat('%', #{ ybReconsiderResetDataView.reconsiderApplyId} ,'%') and appealResultId like concat('%', #{ ybReconsiderResetDataView.appealResultId} ,'%') and verifyId like concat('%', #{ ybReconsiderResetDataView.verifyId} ,'%') and manageId like concat('%', #{ ybReconsiderResetDataView.manageId} ,'%') and arDoctorCode like concat('%', #{ ybReconsiderResetDataView.arDoctorCode} ,'%') and arDoctorName like concat('%', #{ ybReconsiderResetDataView.arDoctorName} ,'%') and arDeptCode like concat('%', #{ ybReconsiderResetDataView.arDeptCode} ,'%') and arDeptName like concat('%', #{ ybReconsiderResetDataView.arDeptName} ,'%') and typeno =#{ ybReconsiderResetDataView.typeno} and operateType =#{ ybReconsiderResetDataView.operateType}
