mapper.update:
update yb_reconsider_verify
applyDataId=#{applyDataId}, verifyDoctorCode=#{verifyDoctorCode}, verifyDoctorName=#{verifyDoctorName}, verifyDeptCode=#{verifyDeptCode}, verifyDeptName=#{verifyDeptName}, dataType=#{dataType}, applyDateStr=#{applyDateStr}, typeno=#{typeno}, orderNumber=#{orderNumber}, orderNum=#{orderNum}, orderDoctorCode=#{orderDoctorCode}, orderDoctorName=#{orderDoctorName}, orderDeptCode=#{orderDeptCode}, orderDeptName=#{orderDeptName}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, areaType=#{areaType},
where id=#{id}

mapper.select:
select  *  from yb_reconsider_verify
where  1=1
 and id =#{ ybReconsiderVerify.id} 
 and applyDataId =#{ ybReconsiderVerify.applyDataId} 
     and verifyDoctorCode like concat('%', #{ ybReconsiderVerify.verifyDoctorCode} ,'%')
     and verifyDoctorName like concat('%', #{ ybReconsiderVerify.verifyDoctorName} ,'%')
     and verifyDeptCode like concat('%', #{ ybReconsiderVerify.verifyDeptCode} ,'%')
     and verifyDeptName like concat('%', #{ ybReconsiderVerify.verifyDeptName} ,'%')
     and COMMENTS like concat('%', #{ ybReconsiderVerify.comments} ,'%')
 and STATE =#{ ybReconsiderVerify.state} 
 and IS_DELETEMARK =#{ ybReconsiderVerify.isDeletemark} 
and ybReconsiderVerify.areaType =#{ ybReconsiderVerify.areaType}

SELECT
    *
FROM
    yb_reconsider_verify
WHERE
    applyDateStr = #{ applyDateStr }
 and areaType =#{ areaType } 
 and state =#{ state } 
 and typeno =#{ typeno } 
 and dataType =#{ dataType }
