select ad.pid, ad.serialNo, ad.billNo, ad.proposalCode, ad.projectCode, ad.projectName, ad.num, ad.medicalPrice, ad.ruleName, ad.deductPrice, ad.deductReason, ad.repaymentReason, ad.doctorName, ad.deptCode, ad.deptName, ad.enterHospitalDateStr, ad.outHospitalDateStr, ad.costDateStr, ad.hospitalizedNo, ad.treatmentMode, ad.settlementDateStr, ad.personalNo, ad.insuredName, ad.cardNumber, ad.areaName, ad.versionNumber, ad.backAppeal, ad.typeno, ad.insuredType, ad.dataType, ad.orderNumber, ad.orderNum, rv.applyDateStr, case when rv.id is null then UUID() else rv.id end id, ad.id applyDataId, rv.verifyDoctorCode, concat(rv.verifyDoctorCode,'-',rv.verifyDoctorName) verifyDoctorName, rv.verifyDeptCode, concat(rv.verifyDeptCode,'-',rv.verifyDeptName) verifyDeptName, rv.operateReason, rv.operateDate, rv.matchPersonId, rv.matchPersonName, rv.matchDate, rv.reviewerId, rv.reviewerName, rv.reviewerDate, rv.sendPersonId, rv.sendPersonName, rv.sendDate, rv.STATE, case when rv.id is null then '' else rv.orderDoctorCode end orderDoctorCode, case when rv.id is null then '' else rv.orderDoctorName end orderDoctorName, case when rv.id is null then '' else rv.orderDeptCode end orderDeptCode, case when rv.id is null then '' else rv.orderDeptName end orderDeptName, case when rv.id is null then 0 else 1 end isVerify, case when yp.id is null then 0 else 1 end isPerson, rv.areaType from yb_reconsider_verify rv inner JOIN yb_reconsider_apply_data ad ON ad.id = rv.applyDataId and ad.pid = #{ ybReconsiderVerifyView.pid}
and ad.projectName like concat('%', #{ ybReconsiderVerifyView.projectName} ,'%')
and ad.projectName = #{ ybReconsiderVerifyView.projectName}
and ad.projectName not like concat('%', #{ ybReconsiderVerifyView.projectName} ,'%')
and ad.ruleName like concat('%', #{ ybReconsiderVerifyView.ruleName} ,'%')
and ad.ruleName = #{ ybReconsiderVerifyView.ruleName}
and ad.ruleName not like concat('%', #{ ybReconsiderVerifyView.ruleName} ,'%')
LEFT join yb_person yp on rv.verifyDoctorCode = yp.personCode where rv.applyDateStr = #{ ybReconsiderVerifyView.applyDateStr} and rv.state =#{ ybReconsiderVerifyView.state} and rv.typeno =#{ ybReconsiderVerifyView.typeno} and rv.dataType =#{ ybReconsiderVerifyView.dataType}
and rv.areaType = #{ ybReconsiderVerifyView.areaType}
and rv.verifyDeptName like concat('%', #{ ybReconsiderVerifyView.verifyDeptName} ,'%')
and rv.verifyDeptName = #{ ybReconsiderVerifyView.verifyDeptName}
and rv.verifyDeptName not like concat('%', #{ ybReconsiderVerifyView.verifyDeptName} ,'%')
and rv.orderNumber like concat('%', #{ ybReconsiderVerifyView.orderNumber} ,'%')
and rv.orderNumber = #{ ybReconsiderVerifyView.orderNumber}
and rv.orderNumber not like concat('%', #{ ybReconsiderVerifyView.orderNumber} ,'%')

